SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE valida_acesso_case ( nr_case_p text, cd_pessoa_fisica_P text) AS $body$
DECLARE



qt_existente_w		bigint;
cd_pessoa_fisica_w varchar(100);


BEGIN

select  count(*)
into STRICT    qt_existente_w
FROM   	atendimento_paciente a ,
		episodio_paciente b,
		pf_codigo_externo c
WHERE  	a.nr_seq_episodio = b.nr_sequencia
AND		b.cd_pessoa_fisica = c.cd_pessoa_fisica
AND		c.cd_pessoa_fisica_externo = cd_pessoa_fisica_P
AND 	b.nr_episodio = nr_case_p
AND     coalesce(c.cd_estabelecimento, wheb_usuario_pck.get_cd_estabelecimento) = wheb_usuario_pck.get_cd_estabelecimento;

if (qt_existente_w = 0) then

	--'Codigo de pessoa fisica '||cd_pessoa_fisica_p||' incompativel com o numero de acesso '||nr_case_p
	CALL wheb_mensagem_pck.exibir_mensagem_abort(891544 ,';NR_CASE='||nr_case_p);

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE valida_acesso_case ( nr_case_p text, cd_pessoa_fisica_P text) FROM PUBLIC;

