SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE valida_atendimento_dispensacao (nr_ordem_p can_ordem_prod.nr_sequencia%type) AS $body$
DECLARE

nr_atendimento_w atendimento_paciente.nr_atendimento%type;
dt_cancelamento_w atendimento_paciente.dt_cancelamento%type;


BEGIN

    begin
        select      coalesce(atendimento.nr_atendimento, prescricao.nr_atendimento) nr_atendimento,
                    atendimento.dt_cancelamento
        into STRICT        nr_atendimento_w,
                    dt_cancelamento_w
        from        can_ordem_prod ordem
        inner join  prescr_medica prescricao on ordem.nr_prescricao = prescricao.nr_prescricao
        inner join  atendimento_paciente atendimento on coalesce(ordem.nr_atendimento, prescricao.nr_atendimento) = atendimento.nr_atendimento
        where       ordem.nr_sequencia = nr_ordem_p;
    exception when no_data_found then
        nr_atendimento_w := null;
        dt_cancelamento_w := null;
    end;

    if (dt_cancelamento_w IS NOT NULL AND dt_cancelamento_w::text <> '') then
        -- Nao e possivel realizar a dispensacao pois o atendimento {{atendimento}} encontra-se cancelado
        CALL wheb_mensagem_pck.exibir_mensagem_abort(1184977, 'NR_ATENDIMENTO=' || nr_atendimento_w);
    end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
