CREATE OR REPLACE PROCEDURE valida_cod_usuario_conv (cd_convenio_p bigint, cd_categoria_p text, ie_tipo_atendimento_p bigint, ds_procedure_p INOUT text) AS $body$
DECLARE


ds_procedure_w		varchar(255)	:= null;
cd_categoria_w		varchar(10);
ie_tipo_atendimento_w	smallint;

c01 CURSOR FOR
	SELECT	ds_procedure_validacao
	from	regra_validacao_usuario
	where	cd_convenio					= cd_convenio_p
	and	coalesce(cd_categoria, cd_categoria_w)		= cd_categoria_w
	and	coalesce(ie_tipo_atendimento, ie_tipo_atendimento_w)	= ie_tipo_atendimento_w;


BEGIN

cd_categoria_w		:= coalesce(cd_categoria_p, '0');
ie_tipo_atendimento_w	:= coalesce(ie_tipo_atendimento_p, 0);

open	c01;
loop
fetch	c01 into
	ds_procedure_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	ds_procedure_w	:= ds_procedure_w;

	end;
end loop;
close c01;

ds_procedure_p		:= ds_procedure_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
