CREATE OR REPLACE PROCEDURE valida_dose_mistura ( nr_seq_ordem_p bigint, ie_valida_p INOUT text) AS $body$
DECLARE



cd_material_w			integer;
qt_dose_w				double precision;
cd_unidade_medida_w		varchar(30);
qt_dose_real_w			double precision;
cd_unidade_medida_real_w	varchar(30);


C01 CURSOR FOR
	SELECT	cd_material,
		obter_conversao_unid_med_cons(cd_material,cd_unidade_medida,qt_dose) qt_dose,
		cd_unidade_medida,
		obter_conversao_unid_med_cons(cd_material,cd_unidade_medida_real,sum(qt_dose_real)) ,
		cd_unidade_medida_real
	from	can_ordem_prod_mat
	where	nr_seq_ordem	= nr_seq_ordem_p
	group by cd_material,
		qt_dose,
		cd_unidade_medida,
		cd_unidade_medida_real;


BEGIN

ie_valida_p	:= 'S';

open C01;
loop
	fetch C01 into
		cd_material_w,
		qt_dose_w,
		cd_unidade_medida_w,
		qt_dose_real_w,
		cd_unidade_medida_real_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */

	if (round((qt_dose_w)::numeric,3) <> round((qt_dose_real_w)::numeric,3)) then
		ie_valida_p	:= 'N';
	end if;

end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
