SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE valida_ficha_admissao ( nr_prontuario_p bigint, nr_registro_p text, nr_sequencia_p bigint, ds_retorno_p INOUT text) AS $body$
DECLARE



qt_registro_w	integer;
ie_consiste_tumor_w		varchar(1);

BEGIN

ie_consiste_tumor_w := Obter_Param_Usuario(3131, 15, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_consiste_tumor_w);

if (nr_prontuario_p IS NOT NULL AND nr_prontuario_p::text <> '') then
	select	count(*)
	into STRICT	qt_registro_w
	from	can_ficha_admissao
	where	nr_prontuario 	= nr_prontuario_p
	and	nr_registro	= nr_registro_p
	and	nr_sequencia	<> nr_sequencia_p;

	if (qt_registro_w > 0) then
		ds_retorno_p	:= obter_desc_expressao(613137,null);
	end if;


	if (ie_consiste_tumor_w = 'S') then
		select	count(*)
		into STRICT	qt_registro_w
		from	can_ficha_admissao
		where	nr_prontuario		= nr_prontuario_p
		and	ie_mais_um_tu_prim	= 2
		and	nr_sequencia		<> nr_sequencia_p;

		if (qt_registro_w > 0) then
			ds_retorno_p	:= obter_desc_expressao(612959,null);
		end if;
	end if;
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
