CREATE OR REPLACE PROCEDURE valida_gerar_estornar_alta ( cd_perfil_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, cd_funcao_p bigint, ie_alta_estorno_p text, nr_atendimento_p bigint, qt_contas_abertas_p INOUT bigint, permite_conta_particular_p INOUT text) AS $body$
DECLARE

 
permite_conta_particular_w	varchar(255);
qt_contas_abertas_w		bigint;

 

BEGIN 
 
if (cd_perfil_p IS NOT NULL AND cd_perfil_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (cd_funcao_p IS NOT NULL AND cd_funcao_p::text <> '') and (ie_alta_estorno_p IS NOT NULL AND ie_alta_estorno_p::text <> '') then 
	begin 
	select	coalesce(obter_valor_param_usuario(3111, 34, cd_perfil_p, nm_usuario_p, cd_estabelecimento_p), 'N') 
	into STRICT	permite_conta_particular_w 
	;
	 
	 
	if	((permite_conta_particular_w 	= 'T') or 
		(permite_conta_particular_w 	= 'N' AND cd_funcao_p = 3111) or (permite_conta_particular_w 	= 'F') or 
		(permite_conta_particular_w 	= 'A' AND cd_funcao_p = 3111)) and (ie_alta_estorno_p 		= 'A') then 
		begin 
		select count(*) 
		into STRICT	qt_contas_abertas_w 
		from	conta_paciente 
		where	nr_atendimento = nr_atendimento_p 
		and	obter_tipo_convenio(cd_convenio_parametro) = 1 
		and	ie_status_acerto = 1;
		 
		end;
	end if;
	end;
end if;	
 
qt_contas_abertas_p		:= qt_contas_abertas_w;
permite_conta_particular_p	:= permite_conta_particular_w;
 
 
commit;
 
end;
$body$
