CREATE OR REPLACE PROCEDURE valida_material_ccusto_qt ( cd_material_p bigint, cd_centro_custo_p bigint, ie_permite_p INOUT text, cd_local_estoque_p bigint, cd_operacao_estoque_p bigint, qt_quantidade_p bigint, qt_regra_p INOUT bigint) AS $body$
DECLARE


cd_grupo_material_w		smallint;
cd_subgrupo_material_w		smallint;
cd_classe_material_w		integer;
nr_seq_familia_w			bigint;
ie_exclui_w			varchar(01);
qt_maxima_w			double precision;

c030 CURSOR FOR
	SELECT	ie_exclui,
		qt_maxima
	from	centro_custo_material a
	where	cd_centro_custo					= cd_centro_custo_p
	and	ie_requisicao					= 'S'
	and	coalesce(a.nr_seq_familia, nr_seq_familia_w)			= nr_seq_familia_w
	and	coalesce(a.cd_material, cd_material_p)			= cd_material_p
	and	coalesce(a.cd_classe_material, cd_classe_material_w)		= cd_classe_material_w
	and	coalesce(a.cd_subgrupo_material, cd_subgrupo_material_w)	= cd_subgrupo_material_w
	and	coalesce(a.cd_grupo_material, cd_grupo_material_w)		= cd_grupo_material_w
	and	coalesce(a.cd_local_estoque, coalesce(cd_local_estoque_p,0))	= coalesce(cd_local_estoque_p,0)
	and	coalesce(a.cd_operacao_estoque, coalesce(cd_operacao_estoque_p,0)) = coalesce(cd_operacao_estoque_p,0)
	and	coalesce(a.qt_maxima, 0) > 0
	order by
		coalesce(a.nr_seq_familia, 0),
		coalesce(a.cd_material, 0),
	  	coalesce(a.cd_classe_material, 0),
	  	coalesce(a.cd_subgrupo_material, 0),
	  	coalesce(a.cd_grupo_material, 0);


BEGIN

select 	cd_grupo_material,
	cd_subgrupo_material,
	cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from 	estrutura_material_v
where	cd_material	= cd_material_p;

select	coalesce(max(nr_seq_familia), 0)
into STRICT	nr_seq_familia_w
from	material
where	cd_material	= cd_material_p;

ie_exclui_w		:= 'S';

open C030;
loop
fetch C030 into
	ie_exclui_w,
	qt_maxima_w;
EXIT WHEN NOT FOUND; /* apply on C030 */
	ie_exclui_w		:= ie_exclui_w;
end loop;
close C030;

ie_permite_p	:= 'S';
if (qt_quantidade_p > qt_maxima_w) then
	ie_permite_p	:= 'N';
	qt_regra_p	:= qt_maxima_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
