SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE valida_vinc_onco_autorizacao ( nm_usuario_p text, nr_seq_atendimento_p bigint, nr_atendimento_p bigint, ie_acao_p text) AS $body$
DECLARE


ie_vincular_atend_aut_w varchar(5);
ds_dia_ciclo_w paciente_atendimento.ds_dia_ciclo%type;
nr_ciclo_w paciente_atendimento.nr_ciclo%type;
nr_seq_paciente_w paciente_atendimento.nr_seq_paciente%type;


BEGIN

ie_vincular_atend_aut_w := obter_param_usuario(865, 184, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ie_vincular_atend_aut_w);

if (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') then

    select ds_dia_ciclo,
           nr_ciclo,
           nr_seq_paciente
    into STRICT ds_dia_ciclo_w,
         nr_ciclo_w,
         nr_seq_paciente_w
    from paciente_atendimento
    where nr_seq_atendimento = nr_seq_atendimento_p;

    if ((nr_ciclo_w IS NOT NULL AND nr_ciclo_w::text <> '') and (ds_dia_ciclo_w IS NOT NULL AND ds_dia_ciclo_w::text <> '') and (nr_seq_paciente_w IS NOT NULL AND nr_seq_paciente_w::text <> '')) then

        if ((ie_acao_p = 'VA' or ie_acao_p = 'GA') AND nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then

            update  autorizacao_convenio
            set     dt_atualizacao = clock_timestamp(),
                    nm_usuario = nm_usuario_p,
                    nr_atendimento = nr_atendimento_p
            where   nr_seq_paciente_setor = nr_seq_paciente_w
            and     coalesce(nr_atendimento::text, '') = ''
            and     ((ie_vincular_atend_aut_w = 'T')
                    or ((ie_vincular_atend_aut_w = 'S' or ie_tipo_dia = 'C')  and nr_ciclo = nr_ciclo_w )
                    or ((ie_vincular_atend_aut_w = 'D' and ie_tipo_dia = 'D') and ds_dia_ciclo = ds_dia_ciclo_w and nr_ciclo = nr_ciclo_w));

        elsif (ie_acao_p = 'DA') then

            update  autorizacao_convenio
            set     dt_atualizacao = clock_timestamp(),
                    nm_usuario = nm_usuario_p,
                    nr_atendimento  = NULL
            where   nr_seq_paciente_setor = nr_seq_paciente_w
            and     ((ie_vincular_atend_aut_w = 'T')
                    or ((ie_vincular_atend_aut_w = 'S' or ie_tipo_dia = 'C')  and nr_ciclo = nr_ciclo_w )
                    or ((ie_vincular_atend_aut_w = 'D' and ie_tipo_dia = 'D') and ds_dia_ciclo = ds_dia_ciclo_w and nr_ciclo = nr_ciclo_w));

        end if;
    end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
