SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE valores_classif_susp ( ie_opcao_p text, qt_classif_p INOUT bigint, qt_motivo_p INOUT bigint) AS $body$
DECLARE


qt_classif_w	bigint := 0;
qt_motivo_w	bigint := 0;
cd_perfil_w integer;


BEGIN

cd_perfil_w := Obter_perfil_ativo;

select 	coalesce(max(nr_sequencia), 0)
into STRICT	qt_classif_w
from   	classif_suspensao_prescr
where  	ie_padrao = 'S'
and	   coalesce(cd_perfil,cd_perfil_w) = cd_perfil_w;

select 	coalesce(max(nr_sequencia), 0)
into STRICT	qt_motivo_w
from   	motivo_suspensao_prescr
where  	ie_opcao  = ie_opcao_p
and    	ie_padrao = 'S';

qt_classif_p 	:= qt_classif_w;
qt_motivo_p	:= qt_motivo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
