SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE verificar_classif_liberada ( cd_pessoa_fisica_p text, nm_usuario_p text, cd_estabelecimento_p bigint ) AS $body$
DECLARE


nr_seq_classif_w		bigint := 0;
ie_classif_liberada_w	bigint;
nr_seq_classif_pf_w	bigint;
ds_classificacao_w		varchar(255);


BEGIN

ie_classif_liberada_w := obter_param_usuario(-2, 30, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_classif_liberada_w);

if (ie_classif_liberada_w IS NOT NULL AND ie_classif_liberada_w::text <> '') then
	begin

	select	count(*)
	into STRICT	nr_seq_classif_w
	from	pessoa_classif
	where	nr_seq_classif    in (ie_classif_liberada_w)
	and   	cd_pessoa_fisica  = cd_pessoa_fisica_p;

   	if (nr_seq_classif_w = 0 ) then
		begin

		select	max(nr_seq_classif)
		into STRICT	nr_seq_classif_pf_w
		from	pessoa_classif
		where	cd_pessoa_fisica  = cd_pessoa_fisica_p;



		if (nr_seq_classif_pf_w > 0) then
			begin

			select	max(ds_classificacao)
			into STRICT	ds_classificacao_w
			from	classif_pessoa
			where	nr_sequencia  = nr_seq_classif_pf_w;

			if (ds_classificacao_w <> '') then
				begin
				RAISE EXCEPTION '%', substr(obter_texto_tasy(103035, wheb_usuario_pck.get_nr_seq_idioma),1,255) || ' ' || ds_classificacao_w || '! #@#@' USING ERRCODE = '45011';
				end;
			end if;
			end;

		end if;

		RAISE EXCEPTION '%', substr(obter_texto_tasy(103036, wheb_usuario_pck.get_nr_seq_idioma),1,255) || '#@#@' USING ERRCODE = '45011';

		end;
	end if;
         end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
