CREATE OR REPLACE PROCEDURE verificar_gerenciador_ativo () AS $body$
DECLARE

cnt_w integer;

BEGIN
  select count(*) into STRICT cnt_w from v$session where action like wheb_mensagem_pck.get_texto(458037);
  if (cnt_w = 0) then
	begin
    CALL GERAR_EVENTO_GERENCIADOR('INGI', wheb_mensagem_pck.get_texto(458045) ||' '|| to_char(clock_timestamp(), 'dd/MM/yyyy hh24:mi:ss'));
	end;
  elsif (cnt_w > 1) then
	begin
    CALL GERAR_EVENTO_GERENCIADOR('INGI', wheb_mensagem_pck.get_texto(458048) ||' '|| to_char(clock_timestamp(), 'dd/MM/yyyy hh24:mi:ss'));
	end;
  end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
