SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE verificar_regras_onc (nr_sequencia_p bigint, nr_seq_material_p bigint, ie_pasta_p text) AS $body$
DECLARE

 
cd_perfil_w			integer;
nr_seq_regra_w		varchar(15);
nm_usuario_w		varchar(20);			
							  
c01 CURSOR FOR 
SELECT	b.nr_sequencia 
from	regra_consiste_onc_par a, 
    regra_consiste_onc B 
where	a.nr_seq_regra = b.nr_sequencia 
and   coalesce(a.ie_consistir_novo_medic,'N') = 'S' 
and   coalesce(a.cd_perfil,cd_perfil_w)	= cd_perfil_w 
order by	nr_sequencia;							
 

BEGIN 
 
cd_perfil_w := obter_perfil_ativo;
nm_usuario_w := obter_usuario_ativo;
 
delete from paciente_atendimento_erro 
where	nr_seq_paciente	= nr_sequencia_p;
 
delete from paciente_atendimento_erro 
where	nr_seq_atendimento	= nr_sequencia_p;
		 
commit;
 
 
open c01;
loop 
   fetch c01 into nr_seq_regra_w;
   EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	if (ie_pasta_p = 'P') then 
					 
		CALL consistir_regra_prot_medic(nr_sequencia_p, 
					        nr_seq_material_p, 
					        nm_usuario_w, 
					        nr_seq_regra_w);
 
								  
		 
	elsif (ie_pasta_p = 'A') then 
	 
		CALL consistir_regra_atend_medic(nr_sequencia_p, 
								  nr_seq_material_p, 
			            nm_usuario_w, 
			            nr_seq_regra_w);
		 
	end if;
	end;
    
end loop;
close c01; 	
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
