SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE verificar_se_ap_lote_bloqueio ( nr_seq_lote_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


nr_sequencia_w		bigint;
nm_usuario_w		varchar(15);
dt_atualizacao_w		timestamp;
dt_tentativa_w		timestamp;
ie_bloqueado_w		varchar(1);
qt_min_controle_lote_w	bigint;
qt_min_diferenca_w		double precision;
ds_retorno_w		varchar(255);
ds_reason_w         varchar(255);
ds_hold_status_w   varchar(255);


BEGIN


ie_bloqueado_w	:= 'N';

select	coalesce(max(qt_min_controle_lote), 0)
into STRICT	qt_min_controle_lote_w
from	parametros_farmacia
where	cd_estabelecimento		= cd_estabelecimento_p;

if (qt_min_controle_lote_w > 0) then
	begin

	delete
	from	ap_lote_bloqueio
	where	nm_usuario	= nm_usuario_p
	and	nr_seq_lote	<> nr_seq_lote_p
	and (coalesce(ie_explicit_hold::text, '') = '' or ie_explicit_hold<>'S');

	select	coalesce(max(nr_sequencia), 0)
	into STRICT	nr_sequencia_w
	from	ap_lote_bloqueio
	where	nr_seq_lote	= nr_seq_lote_p;

	if (nr_sequencia_w = 0) then

		insert into ap_lote_bloqueio(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			nr_seq_lote)
		values (	nextval('ap_lote_bloqueio_seq'),
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_lote_p);

		ie_bloqueado_w	:= 'N';
	else
		begin

		select	nm_usuario,
			dt_atualizacao
		into STRICT	nm_usuario_w,
			dt_atualizacao_w
		from	ap_lote_bloqueio
		where	nr_sequencia	= nr_sequencia_w;

		if (nm_usuario_w = nm_usuario_p) then

			update	ap_lote_bloqueio
			set	dt_atualizacao	= clock_timestamp()
			where	nr_sequencia	= nr_sequencia_w;

			ie_bloqueado_w	:= 'N';

		elsif (nm_usuario_w <> nm_usuario_p) then
			begin

			dt_tentativa_w		:= clock_timestamp();
			qt_min_diferenca_w	:= ((dt_tentativa_w - dt_atualizacao_w) * 1440);


			if (qt_min_diferenca_w < qt_min_controle_lote_w) then
				ie_bloqueado_w	:= 'S';
			else
				update	ap_lote_bloqueio
				set	nm_usuario	= nm_usuario_p,
					dt_atualizacao	= dt_tentativa_w
				where	nr_sequencia	= nr_sequencia_w;

				ie_bloqueado_w	:= 'N';
			end if;

			end;
		end if;
		end;
	end if;
	end;
end if;

begin
   select IE_MANUAL_HOLD_BY_USER  into STRICT ds_hold_status_w from
  ap_lote_bloqueio where nr_seq_lote = nr_seq_lote_p;
  exception when others then
    ds_hold_status_w := 'N';
  end;

  if (ds_hold_status_w = 'S') then 
    dt_tentativa_w		:= clock_timestamp();
  end if;


if (ie_bloqueado_w = 'S' OR ds_hold_status_w = 'S') then
	  select max(ds_block_reason) into STRICT ds_reason_w from
  ap_lote_bloqueio where nr_seq_lote = nr_seq_lote_p;

	ds_erro_p		:= WHEB_MENSAGEM_PCK.get_texto(281142) || ' ' || nm_usuario_w || '!' ||  chr(10) || chr(13) ||
				WHEB_MENSAGEM_PCK.get_texto(281143) || to_char(dt_atualizacao_w,'dd/mm/yyyy hh24:mi:ss') || chr(13) || chr(10) ||
				WHEB_MENSAGEM_PCK.get_texto(281144) || to_char(dt_tentativa_w, 'dd/mm/yyyy hh24:mi:ss') || chr(13) || chr(10);
                if (coalesce(ds_reason_w::text, '') = '') then
                  ds_erro_p := ds_erro_p || WHEB_MENSAGEM_PCK.get_texto(281145) || qt_min_controle_lote_w || WHEB_MENSAGEM_PCK.get_texto(281146);
                elsif (ds_hold_status_w='S')then
                  ds_erro_p := WHEB_MENSAGEM_PCK.get_texto(1136952) || ' ' || nm_usuario_w || '!' ||  chr(10) || chr(13) ||
                              WHEB_MENSAGEM_PCK.get_texto(1136951) || to_char(dt_atualizacao_w,'dd/mm/yyyy hh24:mi:ss') || chr(13) || chr(10) ||
                              WHEB_MENSAGEM_PCK.get_texto(281144) || to_char(dt_tentativa_w, 'dd/mm/yyyy hh24:mi:ss') || chr(13) || chr(10) ||
                              WHEB_MENSAGEM_PCK.get_texto(1136950) || '= ' || ds_reason_w ||  chr(10) || chr(13);
                else
                  ds_erro_p := ds_erro_p || WHEB_MENSAGEM_PCK.get_texto(135899) || '= ' || ds_reason_w ||  chr(10) || chr(13) || WHEB_MENSAGEM_PCK.get_texto(281145) || qt_min_controle_lote_w || WHEB_MENSAGEM_PCK.get_texto(281146);
                end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
