CREATE OR REPLACE PROCEDURE verificar_se_item_inv_bloqueio ( nr_seq_inventario_p bigint, nr_seq_item_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ie_opcao_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


/* Última documentação 22/10/2011 */

/*
Esta procedure retorna se o item do inventário está bloqueado para determinado usuário.
O bloqueio acontece sempre após a localizacão do item do inventário pelo usuário, se o usuário localizar outro item do inventário o atual é desbloqueado.
Também é tratado um determinado tempo para o desbloqueio automático, pois um usuário pode deixar a tela aberta com um item bloqueado para si.

ie_opcao_p
B -  Bloqueia o item do inventário;
D - Desbloqueia o item do inventário;
*/
nr_sequencia_w			bigint;
nm_usuario_w			varchar(15);
dt_atualizacao_w			timestamp;
dt_tentativa_w			timestamp;
ie_bloqueado_w			varchar(1);
qt_min_controle_inv_w		bigint;
qt_min_diferenca_w			double precision;
ds_retorno_w			varchar(255);


BEGIN

/*Pega o tempo para controle do item do inventário conforme parâmetro [199] do PalmWeb ( Usuário que está realizando a contagem )*/

qt_min_controle_inv_w := obter_param_usuario(88, 199, null, nm_usuario_p, cd_estabelecimento_p, qt_min_controle_inv_w);

if (ie_opcao_p = 'B') then
	begin
	ie_bloqueado_w	:= 'N';

	if (qt_min_controle_inv_w > 0) then
		begin

		delete
		from	inventario_bloqueio
		where	nm_usuario		= nm_usuario_p
		and	nr_seq_inventario 		<> nr_seq_inventario_p
		and	nr_seq_item 		<> nr_seq_item_p;

		select	coalesce(max(nr_sequencia), 0)
		into STRICT	nr_sequencia_w
		from	inventario_bloqueio
		where	nr_seq_inventario 		= nr_seq_inventario_p
		and	nr_seq_item 		= nr_seq_item_p;

		if (nr_sequencia_w = 0) then

			insert into inventario_bloqueio(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				nr_seq_inventario,
				nr_seq_item)
			values (	nextval('inventario_bloqueio_seq'),
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_inventario_p,
				nr_seq_item_p);

			ie_bloqueado_w := 'N';

			/* Altera o status do item para Em Conferência */

			CALL altera_status_item_inventario(nr_seq_item_p, nr_seq_inventario_p, 'E', 'B');
		else
			begin

			select	nm_usuario,
				dt_atualizacao
			into STRICT	nm_usuario_w,
				dt_atualizacao_w
			from	inventario_bloqueio
			where	nr_sequencia = nr_sequencia_w;

			if (nm_usuario_w = nm_usuario_p) then

				update	inventario_bloqueio
				set	dt_atualizacao	= clock_timestamp()
				where	nr_sequencia	= nr_sequencia_w;

				ie_bloqueado_w := 'N';

			elsif (nm_usuario_w <> nm_usuario_p) then
				begin

				dt_tentativa_w		:= clock_timestamp();
				qt_min_diferenca_w	:= ((dt_tentativa_w - dt_atualizacao_w) * 1440);

				ie_bloqueado_w		:= 'S';
				if (qt_min_diferenca_w < qt_min_controle_inv_w) then
					ie_bloqueado_w	:= 'S';
				else
					update	inventario_bloqueio
					set	nm_usuario	= nm_usuario_p,
						dt_atualizacao	= dt_tentativa_w
					where	nr_sequencia	= nr_sequencia_w;

					ie_bloqueado_w	:= 'N';
				end if;

				end;
			end if;
			end;
		end if;
		end;
	end if;

	if (ie_bloqueado_w = 'S') then
		ds_retorno_p := Wheb_mensagem_pck.get_texto(306071, 'NM_USUARIO='||nm_usuario_w || ';' || 'DT_ATUALIZACAO='||to_char(dt_atualizacao_w,'dd/mm/yyyy hh24:mi:ss') );
						/*	Este item está sendo conferido pelo usuário #@NM_USUARIO#@
							em  #@DT_ATUALIZACAO#@ 	*/
	end if;
	end;
elsif (ie_opcao_p = 'D') then
	begin
	select	coalesce(max(nr_sequencia), 0)
	into STRICT	nr_sequencia_w
	from	inventario_bloqueio
	where	nm_usuario	= nm_usuario_p
	and	nr_seq_inventario 	= nr_seq_inventario_p
	and	nr_seq_item 	= nr_seq_item_p;

	if (nr_sequencia_w > 0) then
		delete
		from	inventario_bloqueio
		where	nr_sequencia = nr_sequencia_w;
	end if;

	/* Altera o status do item para Pendente */

	CALL altera_status_item_inventario(nr_seq_item_p, nr_seq_inventario_p, 'P', 'D');
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE verificar_se_item_inv_bloqueio ( nr_seq_inventario_p bigint, nr_seq_item_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ie_opcao_p text, ds_retorno_p INOUT text) FROM PUBLIC;

