SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE verifica_aprazamento_horario ( hr_prim_horario_p text, dt_inicio_prescr_p timestamp, dt_proxima_dose_p timestamp, qt_prim_horario_p bigint, cd_intervalo_p text, nr_atendimento_p bigint, nr_prescricao_p bigint, nr_seq_material_p bigint, qt_dose_p bigint, cd_material_p bigint, ie_via_aplicacao_p text, ds_erro_p INOUT text, ie_atualiza_p INOUT text) AS $body$
DECLARE


nr_ocorrencia_intervalo_w	bigint;
ds_intervalo_w			varchar(100);
ie_segunda_w			varchar(5);
ie_terca_w			varchar(5);
ie_quarta_w			varchar(5);
ie_quinta_w			varchar(5);
ie_sexta_w			varchar(5);
ie_sabado_w			varchar(5);
ie_domingo_w			varchar(5);
ie_operacao_w			varchar(10);
ie_dia_semana_w			smallint;
qt_prim_horario_w		bigint;
dt_dose_anterior_w		timestamp;

BEGIN
if (cd_intervalo_p IS NOT NULL AND cd_intervalo_p::text <> '') then
	select	max(ie_segunda),
		max(ie_terca),
		max(ie_quarta),
		max(ie_quinta),
		max(ie_sexta),
		max(ie_sabado),
		max(ie_domingo),
		max(ie_operacao),
		obter_ocorrencia_intervalo(cd_intervalo_p,24,'H'),
		obter_desc_intervalo(cd_intervalo_p)
	into STRICT	ie_segunda_w,
		ie_terca_w,
		ie_quarta_w,
		ie_quinta_w,
		ie_sexta_w,
		ie_sabado_w,
		ie_domingo_w,
		ie_operacao_w,
		nr_ocorrencia_intervalo_w,
		ds_intervalo_w
	from	intervalo_prescricao
	where	cd_intervalo	= cd_intervalo_p;
	if (ie_operacao_w = 'D') then

		select pkg_date_utils.get_WeekDay(dt_inicio_prescr_p + coalesce(qt_prim_horario_p,0))
		into STRICT	ie_dia_semana_w
		;

		ie_atualiza_p := 'S';
		if (ie_dia_semana_w	= 1) and (ie_domingo_w		= 'N') then
			ds_erro_p := wheb_mensagem_pck.get_texto(278914);
			ie_atualiza_p := 'N';
		elsif (ie_dia_semana_w	= 2) and (ie_segunda_w		= 'N') then
			ds_erro_p := wheb_mensagem_pck.get_texto(278915);
			ie_atualiza_p := 'N';
		elsif (ie_dia_semana_w	= 3) and (ie_terca_w		= 'N') then
			ds_erro_p := wheb_mensagem_pck.get_texto(278916);
			ie_atualiza_p := 'N';
		elsif (ie_dia_semana_w	= 4) and (ie_quarta_w		= 'N') then
			ds_erro_p := wheb_mensagem_pck.get_texto(278917);
			ie_atualiza_p := 'N';
		elsif (ie_dia_semana_w	= 5) and (ie_quinta_w		= 'N') then
			ds_erro_p := wheb_mensagem_pck.get_texto(278918);
			ie_atualiza_p := 'N';
		elsif (ie_dia_semana_w	= 6) and (ie_sexta_w		= 'N') then
			ds_erro_p := wheb_mensagem_pck.get_texto(278919);
			ie_atualiza_p := 'N';
		elsif (ie_dia_semana_w	= 7) and (ie_sabado_w		= 'N') then
			ds_erro_p := wheb_mensagem_pck.get_texto(278920);
			ie_atualiza_p := 'N';
		else
			ds_erro_p := null;
			ie_atualiza_p := 'S';
		end if;
	else
		if (nr_ocorrencia_intervalo_w > 24) and (hr_prim_horario_p <> '  :  ') then

			select	max(a.dt_proxima_dose)
			into STRICT	dt_dose_anterior_w
			from	prescr_medica b,
				prescr_material a
			where	a.nr_prescricao		= b.nr_prescricao
			and	b.nr_atendimento	= nr_atendimento_p
			and	a.cd_material		= cd_material_p
			and	a.cd_intervalo		= cd_intervalo_p
			and	a.qt_dose		= qt_dose_p
			and	a.ie_via_aplicacao	= ie_via_aplicacao_p
			and	a.nr_prescricao		< nr_prescricao_p
			and	b.dt_prescricao		> clock_timestamp() - interval '5 days';

			if (to_date(to_char(dt_inicio_prescr_p,'dd/mm/yyyy')||' '||hr_prim_horario_p||':00', 'dd/mm/yyyy hh24:mi:ss') < dt_inicio_prescr_p) and (coalesce(dt_dose_anterior_w::text, '') = '') then
				ds_erro_p := wheb_mensagem_pck.get_texto(278921, 'DT_INICIO_PRESCR_P=' ||to_char(dt_inicio_prescr_p,'dd/mm/yyyy hh24:mi:ss'));
					ie_atualiza_p := 'N';
			elsif (dt_dose_anterior_w IS NOT NULL AND dt_dose_anterior_w::text <> '') and
				(to_date(to_char(dt_inicio_prescr_p + coalesce(qt_prim_horario_p,0),'dd/mm/yyyy')||' '||hr_prim_horario_p||':00', 'dd/mm/yyyy hh24:mi:ss') < (dt_dose_anterior_w + (nr_ocorrencia_intervalo_w/24))) then
				ds_erro_p :=	wheb_mensagem_pck.get_texto(278921, 'DT_INICIO_PRESCR_P=' ||to_char(dt_proxima_dose_p,'dd/mm/yyyy hh24:mi:ss'));
				ie_atualiza_p := 'N';
			else
				ds_erro_p := null;
				ie_atualiza_p := 'S';
			end if;
		end if;
	end if;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE verifica_aprazamento_horario ( hr_prim_horario_p text, dt_inicio_prescr_p timestamp, dt_proxima_dose_p timestamp, qt_prim_horario_p bigint, cd_intervalo_p text, nr_atendimento_p bigint, nr_prescricao_p bigint, nr_seq_material_p bigint, qt_dose_p bigint, cd_material_p bigint, ie_via_aplicacao_p text, ds_erro_p INOUT text, ie_atualiza_p INOUT text) FROM PUBLIC;

