SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE verifica_dias_internacao_conv (nr_atendimento_p bigint, cd_pessoa_fisica_p text, cd_convenio_p bigint, cd_plano_convenio_p text, cd_categoria_p text, nm_usuario_p text, ds_mensagem_p INOUT text) AS $body$
DECLARE

 
nr_seq_regra_w	bigint;

nr_atendimento_w	bigint;
dt_entrada_w		timestamp;
dt_alta_w		timestamp;
qt_dias_w		integer;
qt_dia_internacao_w	integer;
qt_atual_w		integer;
qt_dias_direito_w	integer;
dt_entrada_atend_w	timestamp;
ie_tipo_atendimento_w	smallint;
ds_dia_direito_w 	varchar(150);

C01 CURSOR FOR 
	SELECT	nr_sequencia 
	from	ALERTA_ATEND_CONV_INTERN 
	where	cd_convenio					= cd_convenio_p 
	and	((coalesce(cd_plano_convenio,cd_plano_convenio_p)	= cd_plano_convenio_p) or (coalesce(cd_plano_convenio::text, '') = '' and coalesce(cd_plano_convenio_p::text, '') = '')) 
	and	coalesce(cd_categoria,cd_categoria_p)		= cd_categoria_p 
	order by coalesce(cd_convenio,0), coalesce(cd_categoria,0), coalesce(cd_plano_convenio,0);
			
C02 CURSOR FOR 
	SELECT	distinct 
		a.nr_atendimento, 
		a.dt_entrada, 
		coalesce(a.dt_alta,clock_timestamp()) 
	from	ALERTA_ATEND_CONV_INTERN c, 
		atend_categoria_convenio b, 
		atendimento_paciente a 
	where	a.nr_atendimento 	= b.nr_atendimento 
	and	b.cd_convenio		= c.cd_convenio 
	and	b.cd_categoria		= coalesce(c.cd_categoria,b.cd_categoria) 
	and	coalesce(b.cd_plano_convenio,0) = coalesce(coalesce(c.cd_plano_convenio,b.cd_plano_convenio),0) 
	and	a.ie_tipo_atendimento 	= 1 
	and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p 
	and	trunc(a.dt_entrada,'year')	= trunc(dt_entrada_atend_w,'year') 
	and	a.nr_atendimento	<> nr_atendimento_p 
	and	c.nr_sequencia		= nr_seq_regra_w	 
	and	ie_tipo_atendimento_w = 1 
	order by 1;			
			 

BEGIN 
 
open C01;
loop 
fetch C01 into	 
	nr_seq_regra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	nr_seq_regra_w	:= nr_seq_regra_w;
	end;
end loop;
close C01;
 
qt_dias_w	:= 0;
 
if (nr_seq_regra_w	> 0) then 
 
	select	max(coalesce(qt_dias_internacao,0)) 
	into STRICT	qt_dia_internacao_w 
	from	alerta_atend_conv_intern 
	where 	nr_sequencia = nr_seq_regra_w;
	 
	select	max(dt_entrada), 
		max(ie_tipo_atendimento) 
	into STRICT	dt_entrada_atend_w, 
		ie_tipo_atendimento_w 
	from	atendimento_paciente 
	where	nr_atendimento = nr_atendimento_p;
	 
	open C02;
	loop 
	fetch C02 into	 
		nr_atendimento_w, 
		dt_entrada_w, 
		dt_alta_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin 
		 
		qt_atual_w	:= trunc(dt_alta_w - dt_entrada_w);			
		qt_dias_w	:= qt_dias_w + Qt_atual_w;
	 
		end;
	end loop;
	close C02;
end if;
 
ds_dia_direito_w	:= null;
 
if (qt_dias_w > 0) then 
 
	qt_dias_direito_w	:= qt_dia_internacao_w - qt_dias_w;
	if (qt_dias_direito_w > 0) then 
		ds_dia_direito_w := substr(obter_texto_dic_objeto(297627, wheb_usuario_pck.get_nr_seq_idioma, 'QT_DIAS_DIREITO_W='||qt_dias_direito_w),1,255);
					--'Tem direito ainda há ' || qt_dias_direito_w || ' de internação'; 
		 
	end if;
 
	ds_mensagem_p	:= substr(obter_texto_dic_objeto(297630, wheb_usuario_pck.get_nr_seq_idioma, 'QT_DIAS_W='||qt_dias_w || 
												   ';QT_DIA_INTERNACAO_W='|| qt_dia_internacao_w || 
												   ';DS_DIA_DIREITO_W='|| coalesce(ds_dia_direito_w, ' ')),1,255);
				--'O paciente ja esteve ' || qt_dias_w || ' dias internado. A regra do convênio permite ' || qt_dia_internacao_w || ' dias de internação.' || ds_dia_direito_w; 
 
end if;
 
if (qt_dias_w = 0) and (qt_dia_internacao_w > 0) and (ie_tipo_atendimento_w = 1) then 
 
	ds_mensagem_p	:= substr(obter_texto_dic_objeto(297631, wheb_usuario_pck.get_nr_seq_idioma, 'QT_DIA_INTERNACAO_W='||qt_dia_internacao_w),1,255);
				--'O paciente tem direito há '|| qt_dia_internacao_w ||' de internação'; 
 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE verifica_dias_internacao_conv (nr_atendimento_p bigint, cd_pessoa_fisica_p text, cd_convenio_p bigint, cd_plano_convenio_p text, cd_categoria_p text, nm_usuario_p text, ds_mensagem_p INOUT text) FROM PUBLIC;

