SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE verifica_dupl_dia_pa2 ( cd_material_p bigint, nr_prescricao_p bigint, nr_seq_material_p bigint, nm_usuario_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


cd_pessoa_fisica_w		varchar(10);
dt_prescricao_w			timestamp;
dt_min_horario_w		timestamp;
dt_horario_w			timestamp;
nr_prescricao_w			bigint;
nm_medico_w				varchar(60);
ie_suspenso_w			varchar(10);
ie_consitir_susp_w		varchar(10);
cd_estabelecimento_w	bigint;
nr_seq_ficha_tecnica_w	bigint;
dt_inicio_prescr_w		timestamp;
dt_validade_prescr_w	timestamp;


C01 CURSOR FOR
SELECT	a.nr_prescricao,
	substr(obter_nome_medico(b.cd_medico,'N'),1,60),
	x.dt_horario
from	material c,
	prescr_material	a,
	prescr_medica b,
	prescr_mat_hor x
where	a.nr_prescricao			= b.nr_prescricao
and	a.cd_material				= c.cd_material
and	a.nr_prescricao				= x.nr_prescricao
and	a.nr_sequencia				= x.nr_seq_material
and	c.nr_seq_ficha_tecnica		= nr_seq_ficha_tecnica_w
and	b.cd_pessoa_fisica			= cd_pessoa_fisica_w
and	x.dt_horario	   			> (dt_min_horario_w -1)
and	b.dt_validade_prescr between dt_inicio_prescr_w and dt_validade_prescr_w
and	a.ie_agrupador				not in (3,7,9)
and	coalesce(a.nr_seq_kit::text, '') = ''
and	((b.nr_prescricao			<> nr_prescricao_p) or (a.nr_sequencia			<> nr_seq_material_p))
and	coalesce(a.ie_modificado,'N')	<> 'S'
and	((ie_consitir_susp_w		= 'S') or (coalesce(a.ie_suspenso,'N') 	= 'N'));


BEGIN

select	max(nr_seq_ficha_tecnica)
into STRICT	nr_seq_ficha_tecnica_w
from	material
where	cd_material	= cd_material_p;

if (nr_seq_ficha_tecnica_w > 0) then
	ie_consitir_susp_w := Obter_Param_Usuario(924, 307, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_w, ie_consitir_susp_w);

	select	dt_inicio_prescr,
			dt_validade_prescr,
		cd_pessoa_fisica,
		cd_estabelecimento
	into STRICT	dt_inicio_prescr_w,
			dt_validade_prescr_w,
		cd_pessoa_fisica_w,
		cd_estabelecimento_w
	from	prescr_medica
	where	nr_prescricao = nr_prescricao_p;

	select 	to_date(to_char((a.dt_inicio_prescr + coalesce(b.qt_dia_prim_hor,0)),'dd/mm/yyyy')|| Obter_prim_DsHorarios(b.ds_horarios),'dd/mm/yyyy hh24:mi:ss')
	into STRICT	dt_min_horario_w
	from	prescr_material b,
			prescr_medica a
	where	a.nr_prescricao 	= nr_prescricao_p
	and		a.nr_prescricao		= b.nr_prescricao
	and		b.nr_sequencia	 	= nr_seq_material_p;

	open C01;
	loop
	fetch C01 into
		nr_prescricao_w,
		nm_medico_w,
		dt_horario_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */

	ds_retorno_p:= substr(ds_retorno_p || PKG_DATE_FORMATERS.TO_VARCHAR(dt_horario_w, 'timestamp', WHEB_USUARIO_PCK.GET_CD_ESTABELECIMENTO, nm_usuario_p) || ' ' || nr_prescricao_w || ' ' || nm_medico_w || chr(10) || chr(13),1,255);

	end loop;
	close C01;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
