CREATE OR REPLACE PROCEDURE verifica_exige_empresa_conv (cd_estabelecimento_p bigint, cd_convenio_p bigint, cd_categoria_p text, cd_plano_p text, ie_tipo_atendimento_p bigint, cd_empresa_p bigint, cd_perfil_p bigint, ie_exige_p INOUT text) AS $body$
DECLARE


qt_obriga_w	bigint;


BEGIN

ie_exige_p	:= 'N';

if (coalesce(cd_empresa_p,0) = 0) then
	select	count(*)
	into STRICT	qt_obriga_w
	from	regra_convenio_empresa
	where	cd_estabelecimento	= cd_estabelecimento_p
	and	cd_convenio		= cd_convenio_p
	and	((cd_categoria		= cd_categoria_p) or (coalesce(cd_categoria::text, '') = ''))
	and	((cd_plano_convenio	= cd_plano_p) or (coalesce(cd_plano_convenio::text, '') = ''))
	and	((ie_tipo_atendimento	= ie_tipo_atendimento_p) or (coalesce(ie_tipo_atendimento::text, '') = ''))
	and	((cd_perfil		                = cd_perfil_p) or (coalesce(cd_perfil::text, '') = ''));
	
	if (qt_obriga_w > 0) then
		ie_exige_p	:= 'S';
	end if;
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
