SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE verifica_historico_obrigatorio ( nr_seq_ordem_p bigint, nr_seq_tipo_p bigint) AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
qt_registro_w		bigint;
ds_tipo_w		varchar(255);

BEGIN

select 	count(1)
into STRICT	qt_registro_w
from	man_ordem_serv_tecnico
where	nr_seq_ordem_serv = nr_seq_ordem_p
and		nr_seq_tipo	= nr_seq_tipo_p;

if (qt_registro_w = 0 ) then
	select	max(substr(obter_desc_expressao(cd_exp_tipo, ds_tipo),1,80))
	into STRICT	ds_tipo_w
	from	man_tipo_hist
	where	nr_sequencia	= nr_seq_tipo_p;

	CALL wheb_mensagem_pck.EXIBIR_MENSAGEM_ABORT(173239,'DS_TIPO='||ds_tipo_w||';NR_ORDEM='||nr_seq_ordem_p||';');
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
