SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE verifica_medic_paciente ( nr_prescricao_p bigint, cd_material_p bigint, ie_padronizado_p text, ds_erro_p INOUT text, ie_continuar_p INOUT text) AS $body$
DECLARE

 
ie_padronizado_w		varchar(1);
ie_prescricao_w		varchar(1);
ie_lib_paciente_w		bigint := 0;
cd_pessoa_fisica_w		varchar(10);
ds_erro_w			varchar(255);
nr_atendimento_w		bigint;
qt_prescricao_w		bigint;
qt_dias_tratamento_w		bigint;
dt_inicio_validade_w		timestamp;
nm_usuario_w		varchar(50);
ie_usa_liberacao_w	varchar(50);
cd_estabelecimento_w	bigint;

 

BEGIN 
 
ds_erro_w := '';
 
ie_continuar_p := 'S';
 
Select	cd_pessoa_fisica, 
	nr_atendimento, 
	nm_usuario, 
	cd_estabelecimento 
into STRICT	cd_pessoa_fisica_w, 
	nr_atendimento_w, 
	nm_usuario_w, 
	cd_estabelecimento_w 
from	prescr_medica 
where	nr_prescricao = nr_prescricao_p;
 
ie_usa_liberacao_w := obter_param_usuario(47, 1, obter_perfil_ativo, nm_usuario_w, cd_estabelecimento_w, ie_usa_liberacao_w);
 
Select	count(*) 
into STRICT	ie_lib_paciente_w 
from	lib_material_paciente 
where	cd_material = cd_material_p 
and	cd_pessoa_fisica = cd_pessoa_fisica_w 
and	((ie_usa_liberacao_w = 'N') or (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')) 
and	coalesce(dt_suspenso::text, '') = '';
 
select	coalesce(max(ie_padronizado),'N') 
into STRICT	ie_padronizado_w 
from	material 
where	cd_material = cd_material_p;
 
select	coalesce(max(cd_estabelecimento),0) 
into STRICT	cd_estabelecimento_w 
from	prescr_medica 
where	nr_prescricao = nr_prescricao_p;
if (cd_estabelecimento_w > 0) then 
	select	substr(obter_se_material_padronizado(cd_estabelecimento_w, cd_material_p),1,1) 
	into STRICT	ie_padronizado_w 
	;
end if;
 
commit;
 
if (ie_padronizado_w = 'N') and 
	--(ie_padronizado_p = 'N') and --> Rafael 16/2/8 OS's 77452 e 82111 
	((ie_padronizado_p = 'O') or 
	 ((ie_padronizado_p = 'R') and (OBTER_PRESCR_NAO_PADRONIZADO(nr_atendimento_w) = 'N'))) and (ie_lib_paciente_w = 0) then 
	ds_erro_w := ds_erro_w || wheb_mensagem_pck.get_texto(279072); -- Este medicamento/material não é padronizado 
	ie_continuar_p := 'N';	
end if;
 
if (ie_lib_paciente_w = 0) and 
	--(ie_padronizado_p = 'N') and --> Rafael 16/2/8 OS's 77452 e 82111 
	((ie_padronizado_p = 'O') or 
	 ((ie_padronizado_p = 'R') and (OBTER_PRESCR_NAO_PADRONIZADO(nr_atendimento_w) = 'N'))) and (ie_padronizado_w = 'N') then 
	ds_erro_w := ds_erro_w || wheb_mensagem_pck.get_texto(279073); -- 'Este medicamento/material não está liberado para o paciente ' 
	ie_continuar_p := 'N';	
end if;	
 
if (ie_lib_paciente_w > 0) and 
	((ie_padronizado_p = 'O') or 
	 ((ie_padronizado_p = 'R') and (OBTER_PRESCR_NAO_PADRONIZADO(nr_atendimento_w) = 'N'))) and (ie_padronizado_w = 'N') and (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then 
 
	Select	coalesce(max(qt_dias_tratamento),0), 
		max(coalesce(dt_inicio_validade,dt_atualizacao)) 
	into STRICT	qt_dias_tratamento_w, 
		dt_inicio_validade_w 
	from	lib_material_paciente 
	where	cd_material = cd_material_p 
	and	cd_pessoa_fisica = cd_pessoa_fisica_w 
	and	((ie_usa_liberacao_w = 'N') or (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')) 
	and	coalesce(dt_suspenso::text, '') = '';
 
	if (qt_dias_tratamento_w > 0) then 
 
		select	count(*) 
		into STRICT	qt_prescricao_w 
		from	prescr_medica a 
		where	a.nr_prescricao = nr_prescricao_p 
		and	trunc(a.dt_prescricao)	>= trunc(dt_inicio_validade_w + qt_dias_tratamento_w) 
		and (coalesce(a.dt_liberacao::text, '') = '' or coalesce(a.dt_liberacao_medico::text, '') = '');
	 
		if (qt_prescricao_w > 0) then 
			ds_erro_w := wheb_mensagem_pck.get_texto(279077);
			-- O prazo de liberação deste medicamento/material já está encerrado, favor verificar com a farmácia a quantidade de dias que o mesmo foi liberado para o paciente 
			ie_continuar_p := 'N';		
		end if;
	end if;
 
end if;
 
if (ie_lib_paciente_w > 0) and 
	--(ie_padronizado_p = 'N') and -- Rafael em 16/2/8 OS's 77452 e 82111 
	((ie_padronizado_p = 'O') or 
	 ((ie_padronizado_p = 'R') and (OBTER_PRESCR_NAO_PADRONIZADO(nr_atendimento_w) = 'N'))) and (ie_padronizado_w = 'N') and (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then 
	Select	max(qt_dias_tratamento), 
		max(coalesce(dt_inicio_validade,dt_atualizacao)) 
	into STRICT	qt_dias_tratamento_w, 
		dt_inicio_validade_w 
	from	lib_material_paciente 
	where	cd_material = cd_material_p 
	and	((ie_usa_liberacao_w = 'N') or (dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')) 
	and	cd_pessoa_fisica = cd_pessoa_fisica_w 
	and	coalesce(dt_suspenso::text, '') = '';
 
	select	count(distinct(trunc(a.dt_prescricao))) 
	into STRICT	qt_prescricao_w 
	from	prescr_material b, 
		prescr_medica a 
	where	a.nr_prescricao = b.nr_prescricao 
	and	b.cd_material = cd_material_p 
	and	a.nr_atendimento = nr_atendimento_w 
	and	trunc(a.dt_prescricao)	>= trunc(dt_inicio_validade_w) 
	and ((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '') or (a.dt_liberacao_medico IS NOT NULL AND a.dt_liberacao_medico::text <> ''));
	 
	if (qt_prescricao_w >= qt_dias_tratamento_w) then 
		ds_erro_w := wheb_mensagem_pck.get_texto(279077);
		ie_continuar_p := 'N';		
	end if;
 
end if;
 
select	coalesce(max(ie_prescricao),'N') 
into STRICT	ie_prescricao_w 
from	material 
where	cd_material = cd_material_p;
if (cd_estabelecimento_w > 0) then 
	select	substr(obter_se_material_prescricao(cd_estabelecimento_w, cd_material_p),1,1) 
	into STRICT	ie_prescricao_w 
	;
end if;
 
if (ie_prescricao_w = 'N') then 
	ds_erro_w := ds_erro_w || wheb_mensagem_pck.get_texto(279078);
	ie_continuar_p := 'N';	
end if;
 
ds_erro_p := ds_erro_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE verifica_medic_paciente ( nr_prescricao_p bigint, cd_material_p bigint, ie_padronizado_p text, ds_erro_p INOUT text, ie_continuar_p INOUT text) FROM PUBLIC;

