CREATE OR REPLACE PROCEDURE verifica_movto_cartao_canc_tef ( vl_transacao_p bigint, ds_comprovante_p text, ds_nsu_aut_tef_p text, ds_id_ambiente_p text, ds_chave_busca_p text, ds_data_fiscal_p text, ds_ref_nsu_host_p text, nr_seq_movto_cartao_p INOUT text) AS $body$
DECLARE

/*
ds_id_ambiente_p 
01 = Software Express
02 = Auttar
*/
										  
										  
nr_seq_movto_cartao_w	varchar(40);	
								  										

BEGIN
insert into log_tasy(cd_log, ds_log, nm_usuario, dt_atualizacao)
values (57985, 'vl_transacao_p = '||vl_transacao_p||
				' - ds_comprovante_p = '||ds_comprovante_p||
				' - ds_nsu_aut_tef_p = '||ds_nsu_aut_tef_p||
				' - ds_id_ambiente_p = '||ds_id_ambiente_p||
				' - ds_chave_busca_p = '||ds_chave_busca_p||
				' - ds_data_fiscal_p = '||ds_data_fiscal_p||
				' - ds_ref_nsu_host_p = '||ds_ref_nsu_host_p, 'TEF', clock_timestamp());

				
if (ds_id_ambiente_p = '01') then

	select	max(nr_sequencia)
	into STRICT	nr_seq_movto_cartao_w
	from    movto_cartao_cr
	where	(vl_transacao)::numeric  = vl_transacao_p
	and		lpad(nr_autorizacao,9,0) = lpad(ds_comprovante_p,9,0);

elsif (ds_id_ambiente_p = '02') then --Esse no primeiro teste com o Sirio deu ok, Esse auttar é o novo. Verificar se para o SE vai funcionar dessa forma ali igual fazia antes ou se será alterado.
	select	max(nr_sequencia)
	into STRICT	nr_seq_movto_cartao_w
	from    movto_cartao_cr
	where	(vl_transacao)::numeric  = vl_transacao_p
	and		lpad(nr_nsu_tef,6,0) = lpad(ds_chave_busca_p,6,0);

end if;

nr_seq_movto_cartao_p := nr_seq_movto_cartao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
