CREATE OR REPLACE PROCEDURE verifica_movto_cartao_com_lote (nr_sequencia_p text, qt_parcela_p INOUT text, nr_movto_p INOUT text, nr_lote_p INOUT text) AS $body$
DECLARE


qt_parcela_w	varchar(30) := '';
nr_movto_w	bigint;
nr_lote_w	bigint;

c1 CURSOR FOR 	SELECT	substr((obter_qt_parcela_cartao(b.nr_sequencia) || '/' || a.qt_parcela),1,30) qt_parcela,
			b.nr_seq_movto,
			b.nr_seq_lote
		from	movto_cartao_cr_parcela b,
			movto_cartao_cr a
		where	b.nr_seq_movto	= a.nr_sequencia
		and	(b.nr_seq_lote IS NOT NULL AND b.nr_seq_lote::text <> '')
		and	obter_se_contido(b.nr_sequencia, nr_sequencia_p) = 'S';


BEGIN

	open c1;
	loop
	fetch c1 into
		qt_parcela_w,
		nr_movto_w,
		nr_lote_w;
	EXIT WHEN NOT FOUND; /* apply on c1 */
		begin

		if (qt_parcela_p IS NOT NULL AND qt_parcela_p::text <> '') then
			qt_parcela_p :=	qt_parcela_p || ',';
		end if;

		if (nr_movto_p IS NOT NULL AND nr_movto_p::text <> '') then
			nr_movto_p := nr_movto_p || ',';
		end if;

		if (nr_lote_p IS NOT NULL AND nr_lote_p::text <> '') then
			nr_lote_p := nr_lote_p || ',';
		end if;

		qt_parcela_p	:= qt_parcela_p	|| qt_parcela_w;
		nr_movto_p	:= nr_movto_p	|| nr_movto_w;
		nr_lote_p	:= nr_lote_p	|| nr_lote_w;
		end;
	end loop;
	close c1;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
