CREATE OR REPLACE PROCEDURE verifica_numero_controle ( nr_atendimento_p bigint, nr_controle_p bigint, ds_parametro_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


cd_pessoa_fisica_w varchar(10);
cd_pessoa_fisica_externo_w varchar(10);
ds_retorno_w varchar(2000) := '';


BEGIN

if (ds_parametro_p = 'PC')  then
	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
		select 	cd_pessoa_fisica
		into STRICT 	cd_pessoa_fisica_w
		from 	atendimento_paciente
		where 	nr_atendimento = nr_atendimento_p;
	end if;

	select  coalesce(max(cd_pessoa_fisica_externo),'XPTO')
	into STRICT	cd_pessoa_fisica_externo_w
	from 	pf_codigo_externo
	where	cd_pessoa_fisica = cd_pessoa_fisica_w
                and coalesce(cd_estabelecimento, wheb_usuario_pck.get_cd_estabelecimento) = wheb_usuario_pck.get_cd_estabelecimento;

	if (cd_pessoa_fisica_externo_w = 'XPTO')  and (coalesce(nr_controle_p,0) <> 0) then
		ds_retorno_w	:=  OBTER_TEXTO_TASY(308208,null);
	end if;
end if;

ds_retorno_p := ds_retorno_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
