SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE verifica_orcamento_paciente ( nr_atendimento_p bigint, cd_convenio_p bigint, cd_categoria_p text) AS $body$
DECLARE

 
qt_orcamento_w	bigint;


BEGIN 
 
Select	count(*) 
into STRICT	qt_orcamento_w 
from	orcamento_paciente 
where	cd_pessoa_fisica = (	SELECT	b.cd_pessoa_fisica 
				from	atendimento_paciente b 
				where	nr_atendimento = nr_atendimento_p) 
and	cd_convenio	= cd_convenio_p 
and	cd_categoria	= cd_categoria_p 
and	dt_validade	> clock_timestamp();
 
if (qt_orcamento_w = 0) then 
	--r.aise_application_error(-20011,'O convênio exige orçamento, mas não existe orçamento para o paciente.' || '#@#@'); 
	CALL wheb_mensagem_pck.exibir_mensagem_abort(263639);
end if;
 
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
