CREATE OR REPLACE PROCEDURE verifica_origem_prontuario ( cd_pessoa_fisica_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


ds_setor_w	varchar(100);



BEGIN

begin
select	substr(obter_nome_setor(max(cd_setor_atendimento)),1,100)
into STRICT	ds_setor_w
from	pf_setor_prontuario
where	cd_pessoa_fisica = cd_pessoa_fisica_p
and	coalesce(dt_final_vinculo::text, '') = '';
exception
	when others then
	ds_setor_w	:= null;
end;

if (ds_setor_w IS NOT NULL AND ds_setor_w::text <> '') then
	ds_retorno_p	:= Wheb_mensagem_pck.get_texto(305970, 'DS_SETOR='||ds_setor_w); -- Paciente do '||ds_setor_w||' favor encaminhar Ã  triagem.
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
