SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE verifica_paciente_ale_adep ( cd_pessoa_fisica_p text, nr_prescricao_p bigint, nr_sequencia_proc_p bigint, ie_retorno_p INOUT text ) AS $body$
DECLARE

 
 
ie_pac_alergia_w		bigint := 0;
nr_seq_ficha_tecnica_w		bigint;
qt_ficha_tec_mat_w		bigint;
nr_seq_dcb_w			bigint;
cd_material_w			integer;
cd_material_ww			integer;
cd_material_generico_w		integer;
cd_classe_mat_w			bigint;
qt_alergia_w			bigint;
cd_classe_material_w		integer;
qt_dcb_w			bigint;
qt_dcb_ficha_w			bigint;
nr_sequencia_dcb_w		bigint;
ds_material_w			varchar(255);

 
 
C01 CURSOR FOR 
	SELECT	nr_seq_ficha_tecnica, 
		nr_seq_dcb, 
		cd_material, 
		cd_classe_mat 
	from	paciente_alergia 
	where	cd_pessoa_fisica	= cd_pessoa_fisica_p 
	and	coalesce(dt_fim::text, '') = '' 
	and	coalesce(dt_inativacao::text, '') = '';
	
 
C02 CURSOR FOR 
	SELECT	nr_seq_ficha_tecnica 
	from	material 
	where	cd_material in (SELECT	cd_material_w 
				 
				
union
 
				select	b.cd_material_generico 
				from	material b 
				where	b.cd_material = cd_material_w 
				
union
 
				select	b.cd_material 
				from	material b 
				where	b.cd_material_generico = cd_material_w);

C03 CURSOR FOR 
	SELECT	a.nr_seq_dcb 
	from	material_dcb a 
	where	a.cd_material in (SELECT	cd_material_w 
					 
					
union
 
					select	b.cd_material_generico 
					from	material b 
					where	b.cd_material = cd_material_w 
					
union
 
					select	b.cd_material 
					from	material b 
					where	b.cd_material_generico = cd_material_w) 
	
union
 
	select	b.nr_seq_dcb 
	from	medic_ficha_tecnica b, 
		material a 
	where	a.cd_material in (select	cd_material_w 
					 
					
union
 
					select	b.cd_material_generico 
					from	material b 
					where	b.cd_material = cd_material_w 
					
union
 
					select	b.cd_material 
					from	material b 
					where	b.cd_material_generico = cd_material_w) 
	and	a.nr_seq_ficha_tecnica	= b.nr_sequencia;

C04 CURSOR FOR 
	SELECT	nr_sequencia, 
		nr_seq_dcb 
	from	medic_ficha_tecnica 
	where	nr_seq_superior		= nr_seq_ficha_tecnica_w;
	
c05 CURSOR FOR 
	SELECT	cd_material 
	from	prescr_material 
	where	nr_prescricao = nr_prescricao_p 
	and	nr_sequencia_proc = nr_sequencia_proc_p;
	
 

BEGIN 
 
ie_retorno_p	:= '';
 
select	count(*) 
into STRICT	qt_alergia_w 
from	paciente_alergia 
where	cd_pessoa_fisica	= cd_pessoa_fisica_p 
and	coalesce(dt_fim::text, '') = '';
 
if (qt_alergia_w > 0) then 
	begin 
	 
	open C05;
	loop 
	fetch C05 into	 
		cd_material_ww;
	EXIT WHEN NOT FOUND; /* apply on C05 */
		begin 
				 
		open c01;
		loop 
		fetch c01 into	 
			nr_seq_ficha_tecnica_w, 
			nr_seq_dcb_w, 
			cd_material_w, 
			cd_classe_mat_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin 
				 
			if (cd_classe_mat_w > 0) then 
				select	cd_classe_material, 
					cd_material 
				into STRICT	cd_classe_material_w, 
					ds_material_w 
				from	estrutura_material_v 
				where	cd_material	= cd_material_ww;
 
				if (cd_classe_material_w = cd_classe_mat_w) then 
					ie_retorno_p	:= ds_material_w;
					exit;
				end if;
			end if;
 
			if (nr_seq_ficha_tecnica_w > 0) then 
				begin 
								 
				select	count(*), 
					max(a.ds_material) 
				into STRICT	qt_ficha_tec_mat_w, 
					ds_material_w 
				from	material a 
				where	a.cd_material in (SELECT	cd_material_ww 
								 
								
union
 
								SELECT	b.cd_material_generico 
								from	material b 
								where	b.cd_material = cd_material_ww 
								
union
 
								select	b.cd_material 
								from	material b 
								where	b.cd_material_generico = cd_material_ww) 
				and	a.nr_seq_ficha_tecnica	= nr_seq_ficha_tecnica_w;				
 
				if (coalesce(qt_ficha_tec_mat_w,0) > 0) then 
					ie_retorno_p	:= ds_material_w;
					exit;
				end if;
 
				select	coalesce(max(nr_seq_dcb),0) 
				into STRICT	nr_sequencia_dcb_w 
				from	medic_ficha_tecnica 
				where	nr_sequencia	= nr_seq_ficha_tecnica_w;
				 
				if (nr_sequencia_dcb_w > 0) then 
					begin 
					select	count(*), 
						max(substr(Obter_Desc_Material(a.cd_material),1,255)) 
					into STRICT	qt_ficha_tec_mat_w, 
						ds_material_w 
					from	material_dcb a 
					where	a.cd_material in (SELECT	cd_material_ww 
									 
									
union
 
									SELECT	b.cd_material_generico 
									from	material b 
									where	b.cd_material = cd_material_ww 
									
union
 
									select	b.cd_material 
									from	material b 
									where	b.cd_material_generico = cd_material_ww) 
					and	a.nr_seq_dcb	= nr_sequencia_dcb_w;					
					 
					if (qt_ficha_tec_mat_w > 0) then 
						ie_retorno_p	:= ds_material_w;
						exit;
					end if;
					end;
				end if;
				 
				end;
			end if;
 
			if (nr_seq_dcb_w > 0) then 
				select	count(*), 
					max(Obter_Desc_Material(a.cd_material)) 
				into STRICT	qt_dcb_w, 
					ds_material_w 
				from	material_dcb a 
				where	a.cd_material in (SELECT	cd_material_ww 
								 
								
union
 
								SELECT	b.cd_material_generico 
								from	material b 
								where	b.cd_material = cd_material_ww 
								
union
 
								select	b.cd_material 
								from	material b 
								where	b.cd_material_generico = cd_material_ww) 
				and	a.nr_seq_dcb	= nr_seq_dcb_w;				
 
				select	count(*), 
					max(a.ds_material) 
				into STRICT	qt_dcb_ficha_w, 
					ds_material_w 
				from	medic_ficha_tecnica b, 
					material a 
				where	a.cd_material in (SELECT	cd_material_ww 
								 
								
union
 
								SELECT	b.cd_material_generico 
								from	material b 
								where	b.cd_material = cd_material_ww 
								
union
 
								select	b.cd_material 
								from	material b 
								where	b.cd_material_generico = cd_material_ww) 
				and	a.nr_seq_ficha_tecnica	= b.nr_sequencia 
				and	b.nr_seq_dcb		= nr_seq_dcb_w;
				 
				if (qt_dcb_w > 0) or (qt_dcb_ficha_w > 0) then 
					ie_retorno_p	:= ds_material_w;
					exit;
				end if;
			end if;
 
			if (cd_material_w > 0) then 
				select	count(*)					 
				into STRICT	qt_alergia_w 
				 
				where	cd_material_ww in (SELECT	cd_material_w 
								 
								
union
 
								SELECT	b.cd_material_generico 
								from	material b 
								where	b.cd_material = cd_material_w 
								
union
 
								select	b.cd_material 
								from	material b 
								where	b.cd_material_generico = cd_material_w);
 
				if (qt_alergia_w > 0) then 
				 
					select Obter_Desc_Material(cd_material_ww) 
					into STRICT ie_retorno_p 
					;
					 
					exit;
				end if;
 
				open c02;
				loop 
				fetch c02 into	 
					nr_seq_ficha_tecnica_w;
				EXIT WHEN NOT FOUND; /* apply on c02 */
					select	count(*), 
						max(a.ds_material) 
					into STRICT	qt_ficha_tec_mat_w, 
						ds_material_w 
					from	material a 
					where	a.cd_material in (SELECT	cd_material_ww 
									 
									
union
 
									SELECT	b.cd_material_generico 
									from	material b 
									where	b.cd_material = cd_material_ww 
									
union
 
									select	b.cd_material 
									from	material b 
									where	b.cd_material_generico = cd_material_ww) 
					and	a.nr_seq_ficha_tecnica	= nr_seq_ficha_tecnica_w;
					 
					if (qt_ficha_tec_mat_w > 0) then 
						ie_retorno_p	:= ds_material_w;
						exit;
					end if;
				 
				end loop;
				close c02;
 
				if (ds_material_w <> '') then 
					exit;
				end if;
			 
				open c03;
				loop 
				fetch c03 into	 
					nr_seq_dcb_w;
				EXIT WHEN NOT FOUND; /* apply on c03 */
 
					select	count(*), 
						max(Obter_Desc_Material(a.cd_material)) 
					into STRICT	qt_dcb_w, 
						ds_material_w 
					from	material_dcb a 
					where	a.cd_material in (SELECT	cd_material_ww 
									 
									
union
 
									SELECT	b.cd_material_generico 
									from	material b 
									where	b.cd_material = cd_material_ww 
									
union
 
									select	b.cd_material 
									from	material b 
									where	b.cd_material_generico = cd_material_ww) 
					and	a.nr_seq_dcb	= nr_seq_dcb_w;
					 
					select	count(*), 
						max(ds_material) 
					into STRICT	qt_dcb_ficha_w, 
						ds_material_w 
					from	medic_ficha_tecnica b, 
						material a 
					where	a.cd_material in (SELECT	cd_material_ww 
									 
									
union
 
									SELECT	b.cd_material_generico 
									from	material b 
									where	b.cd_material = cd_material_ww 
									
union
 
									select	b.cd_material 
									from	material b 
									where	b.cd_material_generico = cd_material_ww) 
					and	a.nr_seq_ficha_tecnica	= b.nr_sequencia 
					and	b.nr_seq_dcb		= nr_seq_dcb_w;					
		 
					if (qt_dcb_w > 0) or (qt_dcb_ficha_w > 0) then 
						ie_retorno_p	:= ds_material_w;
						exit;
					end if;
 
				end loop;
				close c03;
 
				if (ie_retorno_p <> '') then 
					exit;
				end if;
 
			end if;
 
			/* Verificar os derivados correspondentes, caso existam no princÃ­pio ativo - Ivan em 27/02/2008 OS80350 */
 
			open C04;
			loop 
			fetch C04 into	 
				nr_seq_ficha_tecnica_w, 
				nr_seq_dcb_w;
			EXIT WHEN NOT FOUND; /* apply on C04 */
				begin 
				 
				if (nr_seq_ficha_tecnica_w > 0) then 
					select	count(*), 
						max(ds_material) 
					into STRICT	qt_ficha_tec_mat_w, 
						ds_material_w 
					from	material a 
					where	a.cd_material in (SELECT	cd_material_ww 
									 
									
union
 
									SELECT	b.cd_material_generico 
									from	material b 
									where	b.cd_material = cd_material_ww 
									
union
 
									select	b.cd_material 
									from	material b 
									where	b.cd_material_generico = cd_material_ww) 
					and	a.nr_seq_ficha_tecnica	= nr_seq_ficha_tecnica_w;
							 
					if (qt_ficha_tec_mat_w > 0) then 
						ie_retorno_p	:= ds_material_w;
						exit;
					end if;
				end if;
 
				if (nr_seq_dcb_w > 0) then 
					select	count(*), 
						max(Obter_Desc_Material(a.cd_material)) 
					into STRICT	qt_dcb_w, 
						ds_material_w 
					from	material_dcb a 
					where	a.cd_material in (SELECT	cd_material_ww 
									 
									
union
 
									SELECT	b.cd_material_generico 
									from	material b 
									where	b.cd_material = cd_material_ww 
									
union
 
									select	b.cd_material 
									from	material b 
									where	b.cd_material_generico = cd_material_ww) 
					and	a.nr_seq_dcb	= nr_seq_dcb_w;
							 
					select	count(*), 
						max(a.ds_material) 
					into STRICT	qt_dcb_ficha_w, 
						ds_material_w 
					from	medic_ficha_tecnica b, 
						material a 
					where	a.cd_material in (SELECT	cd_material_ww 
									 
									
union
 
									SELECT	b.cd_material_generico 
									from	material b 
									where	b.cd_material = cd_material_ww 
									
union
 
									select	b.cd_material 
									from	material b 
									where	b.cd_material_generico = cd_material_ww) 
					and	a.nr_seq_ficha_tecnica	= b.nr_sequencia 
					and	b.nr_seq_dcb		= nr_seq_dcb_w;
		 
					if (qt_dcb_w > 0) or (qt_dcb_ficha_w > 0) then 
						ie_retorno_p	:= ds_material_w;
						exit;
					end if;
				end if;
 
				end;
			end loop;
			close C04;
 
			end;
		end loop;
		close c01;
		end;
		 
	end loop;
	close C05;
	 
	end;
end if;
 
end;
$body$
