;
-- REVOKE ALL ON PROCEDURE verifica_paciente_ale_adep ( cd_pessoa_fisica_p text, nr_prescricao_p bigint, nr_sequencia_proc_p bigint, ie_retorno_p INOUT text ) FROM PUBLIC;

