SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE verifica_periodo_integral (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

				   
C01 CURSOR FOR 
	SELECT	nr_sequencia, 
		dt_periodo_inicial, 
		dt_periodo_final 
	from	auditoria_conta_paciente 
	where	nr_seq_audit_vinc = nr_sequencia_p 
	order by 2;
	
c01_w	C01%rowtype;

dt_periodo_inicial_w		timestamp;
dt_periodo_final_w		timestamp;
ie_integral_w			varchar(1);
nr_interno_conta_w		bigint;
dt_periodo_inicial_conta_w	timestamp;
dt_periodo_final_conta_w	timestamp;
				

BEGIN 
 
ie_integral_w:= 'S';
 
select 	max(dt_periodo_inicial), 
	max(dt_periodo_final), 
	max(nr_interno_conta) 
into STRICT	dt_periodo_inicial_w, 
	dt_periodo_final_w, 
	nr_interno_conta_w 
from	auditoria_conta_paciente 
where 	nr_sequencia = nr_sequencia_p;
 
select 	max(dt_periodo_inicial), 
	max(dt_periodo_final) 
into STRICT	dt_periodo_inicial_conta_w, 
	dt_periodo_final_conta_w 
from 	conta_paciente 
where 	nr_interno_conta = nr_interno_conta_w;
 
open C01;
loop 
fetch C01 into	 
	c01_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin 
	 
	if (dt_periodo_inicial_w = c01_w.dt_periodo_inicial) and (dt_periodo_final_w  = c01_w.dt_periodo_final) then 
		 
		ie_integral_w:= 'N';
		 
	end if;	
	 
	end;
end loop;
close C01;
 
if (ie_integral_w = 'S') then 
 
	if (dt_periodo_inicial_w <> dt_periodo_inicial_conta_w) and (dt_periodo_final_w <> dt_periodo_final_conta_w) then 
			 
		ie_integral_w:= 'N';
		 
	end if;
 
end if;
 
update	auditoria_conta_paciente 
set 	ie_integral = ie_integral_w 
where 	nr_sequencia = nr_sequencia_p;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
