SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE verifica_permite_add_os_proj ( nr_seq_proj_p bigint, nr_seq_ordem_p bigint) AS $body$
DECLARE


ie_terceiro_os_projeto_w		man_localizacao.ie_terceiro%type;
ie_terceiro_os_w				man_localizacao.ie_terceiro%type;
ie_origem_w						proj_projeto.ie_origem%type;


BEGIN

	select 	a.ie_origem
	into STRICT 	ie_origem_w
	from 	proj_projeto a
	where 	a.nr_sequencia = nr_seq_proj_p;
	
	if (ie_origem_w = 'D') then
	begin

		select 	coalesce(max('S'), 'N') ie_externa
		into STRICT	ie_terceiro_os_projeto_w
		from 	PROJ_PROJETO a,
				man_ordem_servico b,
				man_localizacao c
		where 	a.NR_SEQ_ORDEM_SERV = b.nr_sequencia
		and 	b.nr_seq_localizacao = c.nr_sequencia
		and		a.nr_sequencia = nr_seq_proj_p
		and (c.ie_terceiro = 'S' and not exists (SELECT 1 from man_localizacao_status d
							where 	d.nr_seq_localizacao = c.nr_sequencia 
							and 	b.dt_ordem_servico <= coalesce(d.dt_fim, clock_timestamp())));

		select 	coalesce(max('S'), 'N') ie_externa
		into STRICT	ie_terceiro_os_w
		from 	man_ordem_servico a,
				man_localizacao b
		where 	a.nr_sequencia = nr_seq_ordem_p
		and 	a.nr_seq_localizacao = b.nr_sequencia
		and (b.ie_terceiro = 'S' and not exists (SELECT 1 from man_localizacao_status d
							where 	d.nr_seq_localizacao = b.nr_sequencia 
							and 	a.dt_ordem_servico <= coalesce(d.dt_fim, clock_timestamp())));	


		if (ie_terceiro_os_projeto_w = 'N' and ie_terceiro_os_w = 'S') then
			begin
				CALL wheb_mensagem_pck.exibir_mensagem_abort(1101951);
			end;
		end if;

	end;
	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE verifica_permite_add_os_proj ( nr_seq_proj_p bigint, nr_seq_ordem_p bigint) FROM PUBLIC;

