CREATE OR REPLACE PROCEDURE verifica_prazo_agendamento ( cd_agenda_p text, nr_seq_proc_interno_p bigint, cd_convenio_p bigint, dt_agendamento_p timestamp, hr_inicio_p timestamp, ie_carater_cirurgia_p text, nm_usuario_p text) AS $body$
DECLARE


nr_regra_prazo_w		bigint;
qt_horas_w		bigint;
qt_horas_ww		bigint;
dt_hora_w		timestamp;
ds_mensagem_w		varchar(255);
ds_mensagem_ww		varchar(255);
cd_perfil_w		integer;
qt_tempo_w		bigint;
ie_permite_w		varchar(1);
ie_permite_ww		varchar(1);


BEGIN

qt_tempo_w 	:= trunc(Obter_Hora_Entre_datas(hr_inicio_p,dt_agendamento_p));
cd_perfil_w 	:= Obter_perfil_Ativo;

select	count(*)
into STRICT	nr_regra_prazo_w
from	regra_prazo_agendamento
where	cd_agenda = cd_agenda_p;

if (nr_regra_prazo_w > 0) then
	ie_permite_w := 'N';
	select	coalesce(max('S'),'N')
	into STRICT	ie_permite_w
	from	regra_prazo_agendamento
	where	cd_agenda							= cd_agenda_p
	and	coalesce(nr_seq_proc_interno,coalesce(nr_seq_proc_interno_p,0)) 		= coalesce(nr_seq_proc_interno_p,0)
	and	coalesce(cd_convenio,coalesce(cd_convenio_p,0)) 				= coalesce(cd_convenio_p,0)
	and	coalesce(cd_convenio,coalesce(cd_convenio_p,0)) 				= coalesce(cd_convenio_p,0)
	and	coalesce(ie_carater_cirurgia,coalesce(ie_carater_cirurgia_p,'xpto'))	= coalesce(ie_carater_cirurgia_p,'xpto')
	and	coalesce(cd_perfil,cd_perfil_w) 					= cd_perfil_w
	and	((coalesce(qt_horas,0) 					= 0) or (qt_tempo_w <= qt_horas));

	if (ie_permite_w = 'N') then
		--Não será possível realizar o agendamento. Existem regras de convênio, procedimento ou urgência que não permitem o agendamento.
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(337279);
	end if;
end if;


end;
$body$
