CREATE OR REPLACE PROCEDURE verifica_precaucoes_pendentes ( nm_usuario_p text, nr_atendimento_p bigint, dt_fim_precaucao_p timestamp) AS $body$
DECLARE

			
nr_sequencia_w	bigint;
dt_termino_w timestamp;

C01 CURSOR FOR
	SELECT	coalesce(nr_sequencia,0)
	from	atendimento_precaucao
	where	nr_atendimento = nr_atendimento_p
	and	coalesce(dt_final_precaucao::text, '') = '';

BEGIN

open C01;
loop
fetch C01 into	
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	
	if (nr_sequencia_w > 0) then

		update	atendimento_precaucao
		set	dt_final_precaucao	= dt_fim_precaucao_p,
			dt_termino		= coalesce(dt_termino, dt_fim_precaucao_p),
			dt_atualizacao		= clock_timestamp(),
			nm_usuario		= nm_usuario_p
		where	nr_sequencia		= nr_sequencia_w;
	end if;
	
	end;
	
end loop;
close C01;


if (coalesce(wheb_usuario_pck.get_ie_commit, 'S') = 'S') then commit; end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
