SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE verifica_proced_dupl_dia ( nr_seq_derivado_p bigint, nr_prescricao_p bigint, nm_usuario_p text, ds_retorno_p INOUT text) AS $body$
DECLARE

cd_pessoa_fisica_w	varchar(10);
dt_prescricao_w		timestamp;
nr_prescricao_w		bigint;
nm_medico_w		varchar(60);
ie_suspenso_w		varchar(10);
ie_consitir_susp_w	varchar(10);
cd_estabelecimento_w	bigint;
nr_atendimento_w	bigint;

C01 CURSOR FOR 
SELECT	a.nr_prescricao, 
	substr(obter_nome_medico(b.cd_medico,'N'),1,60), 
	CASE WHEN a.ie_suspenso='S' THEN 'SUSPENSO'  ELSE '' END  
from	prescr_procedimento	a, 
	prescr_medica		b 
where	a.nr_prescricao		= b.nr_prescricao 
and	b.nr_prescricao		<> nr_prescricao_p 
and	a.nr_seq_derivado	= nr_seq_derivado_p 
and	b.cd_pessoa_fisica	= cd_pessoa_fisica_w 
and	b.nr_atendimento	= nr_atendimento_w 
and	trunc(b.dt_prescricao)	= trunc(dt_prescricao_w) 
and	(a.nr_seq_derivado IS NOT NULL AND a.nr_seq_derivado::text <> '') 
and	((ie_consitir_susp_w	= 'S') or (coalesce(a.ie_suspenso,'N') = 'N'));


BEGIN 
 
ie_consitir_susp_w := Obter_Param_Usuario(924, 307, Obter_Perfil_Ativo, nm_usuario_p, cd_estabelecimento_w, ie_consitir_susp_w);
 
select	dt_prescricao, 
	cd_pessoa_fisica, 
	cd_estabelecimento, 
	nr_atendimento 
into STRICT	dt_prescricao_w, 
	cd_pessoa_fisica_w, 
	cd_estabelecimento_w, 
	nr_atendimento_w 
from	prescr_medica 
where	nr_prescricao = nr_prescricao_p;
 
open	C01;
loop 
fetch	C01 into 
	nr_prescricao_w, 
	nm_medico_w, 
	ie_suspenso_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	ds_retorno_p:= substr(ds_retorno_p || ie_suspenso_w || ' ' || nr_prescricao_w || ' ' || nm_medico_w || chr(10) || chr(13),1,255);	
end loop;
close C01;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE verifica_proced_dupl_dia ( nr_seq_derivado_p bigint, nr_prescricao_p bigint, nm_usuario_p text, ds_retorno_p INOUT text) FROM PUBLIC;

