CREATE OR REPLACE PROCEDURE verifica_proc_dupl_dia ( cd_procedimento_p bigint, ie_origem_proced_p bigint, nr_seq_proc_interno_p bigint, nr_prescricao_p bigint, ds_retorno_p INOUT text) AS $body$
DECLARE

					 
cd_pessoa_fisica_w	varchar(10);
dt_prescricao_w		timestamp;
nr_prescricao_w		bigint;
nm_medico_w		varchar(60);
ie_suspenso_w		varchar(10);

C01 CURSOR FOR 
	SELECT	r.nr_prescricao, 
		substr(obter_nome_medico(r.cd_medico,'N'),1,60), 
		CASE WHEN p.ie_suspenso='S' THEN 'SUSPENSO'  ELSE '' END  
	from  	prescr_procedimento p, 
		prescr_medica r 
	where  	p.ie_origem_proced	= ie_origem_proced_p 
	and	p.cd_procedimento 	= cd_procedimento_p 
	and   	p.nr_prescricao 	= r.nr_prescricao 
	and   	r.nr_prescricao 	<> nr_prescricao_p 
	and   	trunc(r.dt_prescricao) 	= dt_prescricao_w 
	and   	r.cd_pessoa_fisica 	= cd_pessoa_fisica_w 
	order by 	r.dt_prescricao;
	
C02 CURSOR FOR 
	SELECT 	r.nr_prescricao, 
		substr(obter_nome_medico(r.cd_medico,'N'),1,60), 
		CASE WHEN p.ie_suspenso='S' THEN 'SUSPENSO'  ELSE '' END  
	from  	prescr_procedimento p, 
		prescr_medica r 
	where  	p.nr_seq_proc_interno	= nr_seq_proc_interno_p 
	and   	p.nr_prescricao 	= r.nr_prescricao 
	and   	r.nr_prescricao 	<> nr_prescricao_p 
	and   	trunc(r.dt_prescricao) 	= dt_prescricao_w 
	and   	r.cd_pessoa_fisica 	= cd_pessoa_fisica_w 
	order by 	r.dt_prescricao;


BEGIN 
 
ds_retorno_p 	:= '';
 
select	trunc(dt_prescricao), 
	cd_pessoa_fisica 
into STRICT	dt_prescricao_w, 
	cd_pessoa_fisica_w 
from	prescr_medica 
where	nr_prescricao = nr_prescricao_p;
 
if (nr_seq_proc_interno_p IS NOT NULL AND nr_seq_proc_interno_p::text <> '')then 
	begin 
	open C02;
	loop 
	fetch C02 into	 
		nr_prescricao_w, 
		nm_medico_w, 
		ie_suspenso_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
				ds_retorno_p:= ds_retorno_p 	|| ie_suspenso_w || ' ' 
								|| nr_prescricao_w || ' ' 
								|| nm_medico_w || chr(10);
	end loop;
	close C02;
	end;
else 
	begin 
	open C01;
	loop 
	fetch C01 into	 
		nr_prescricao_w, 
		nm_medico_w, 
		ie_suspenso_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
				ds_retorno_p:= ds_retorno_p 	|| ie_suspenso_w || ' ' 
								|| nr_prescricao_w || ' ' 
								|| nm_medico_w || chr(10);
	end loop;
	close C01;
	end;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
