SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE verifica_regra_convenio ( nr_protocolo_p bigint, nr_atendimento_p bigint, nr_conta_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

				 
nr_seq_item_w			integer;
cd_convenio_ant_w		integer;
cd_convenio_w			integer;
ds_lista_conv_restr_w		varchar(255);
ds_convenio_ant_w		varchar(255);
ds_convenio_w			varchar(255);
ie_restr_convenio_ant_w		varchar(1);
ie_restr_convenio_w		varchar(1);


BEGIN 
ds_lista_conv_restr_w := obter_param_usuario(290, 61, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ds_lista_conv_restr_w);
 
if (coalesce(ds_lista_conv_restr_w,'0') <> '0') and (coalesce(nr_protocolo_p,0) > 0) and 
	((coalesce(nr_atendimento_p,0) > 0) or (coalesce(nr_conta_p,0) > 0)) then 
	begin	 
	select	max(nr_seq_item) 
	into STRICT	nr_seq_item_w 
	from	protocolo_doc_item 
	where ((nr_documento IS NOT NULL AND nr_documento::text <> '') or (nr_seq_interno IS NOT NULL AND nr_seq_interno::text <> '')) 
	and	nr_sequencia = nr_protocolo_p;
	 
	if (coalesce(nr_seq_item_w,0) > 0) then 
		begin		 
		select	CASE WHEN coalesce(nr_seq_interno,0)=0 THEN obter_convenio_atendimento(nr_documento)  ELSE obter_conv_conta(nr_seq_interno) END , 
			CASE WHEN coalesce(nr_conta_p,0)=0 THEN obter_convenio_atendimento(nr_atendimento_p)  ELSE obter_conv_conta(nr_conta_p) END  
		into STRICT	cd_convenio_ant_w, 
			cd_convenio_w 
		from	protocolo_doc_item 
		where	nr_seq_item = nr_seq_item_w 
		and	nr_sequencia = nr_protocolo_p;
		 
		select	substr(obter_desc_convenio(cd_convenio_ant_w),1,255), 
			substr(obter_desc_convenio(cd_convenio_w),1,255) 
		into STRICT	ds_convenio_ant_w, 
			ds_convenio_w 
		;
		 
		begin 
		select	substr(obter_se_contido(cd_convenio_ant_w,replace(ds_lista_conv_restr_w,';',',')),1,1), 
			substr(obter_se_contido(cd_convenio_w,replace(ds_lista_conv_restr_w,';',',')),1,1) 
		into STRICT	ie_restr_convenio_ant_w, 
			ie_restr_convenio_w 
		;
		exception 
		when others then 
			ie_restr_convenio_ant_w	:= 'N';
			ie_restr_convenio_w	:= 'N';
		end;
 
		if (ie_restr_convenio_w = 'S') and (cd_convenio_w <> cd_convenio_ant_w) and (ie_restr_convenio_ant_w = 'S') then 
			CALL exibir_erro_abortar(obter_desc_expressao(345831)/*'Neste protocolo somente é permitido incluir itens do convênio "'*/
 || ds_convenio_ant_w || '".' || chr(13) || chr(10) || obter_desc_expressao(345832)/*'Verifique o parâmetro [61].'*/,null);
		elsif (ie_restr_convenio_w = 'S') and (cd_convenio_w <> cd_convenio_ant_w) and (ie_restr_convenio_ant_w = 'N') then  
			CALL exibir_erro_abortar(wheb_mensagem_pck.get_texto(804097,'DS_CONVENIO_W=' || ds_convenio_w) || chr(13) || chr(10) || obter_desc_expressao(345832)/*'Verifique o parâmetro [61].'*/,null);
		elsif (ie_restr_convenio_w = 'N') and (ie_restr_convenio_ant_w = 'S') then 
			CALL exibir_erro_abortar(obter_desc_expressao(345831)/*'Neste protocolo somente é permitido incluir itens do convênio "'*/
 || ds_convenio_ant_w || '".' || chr(13) || chr(10) || obter_desc_expressao(345832)/*'Verifique o parâmetro [61].'*/,null);
		end if;
		end;
	end if;	
	end;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
