CREATE OR REPLACE PROCEDURE verifica_regra_serv_reg (nr_seq_tipo_servico_p bigint, cd_estabelecimento_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


ie_regra_w		varchar(1);
ie_convenio_w		varchar(1);


BEGIN
	if (nr_seq_tipo_servico_p IS NOT NULL AND nr_seq_tipo_servico_p::text <> '') then

		select 	max(a.ie_exige_pessoa_atendida),
			max(a.ie_exige_usuario_convenio)
		into STRICT	ie_regra_w,
			ie_convenio_w
		from	eme_tipo_servico a
		where	a.nr_sequencia 	     = nr_seq_tipo_servico_p
		and 	coalesce(a.ie_situacao,'A') = 'A'
		and	a.cd_estabelecimento = cd_estabelecimento_p;

			if (ie_regra_w = 'S') then
			begin
			ds_erro_p := WHEB_MENSAGEM_PCK.get_texto(311698);
			end;
			end if;

			if (ie_convenio_w = 'S') then
			begin
			ds_erro_p := WHEB_MENSAGEM_PCK.get_texto(311699);
			end;
			end if;
	end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
