CREATE OR REPLACE PROCEDURE verifica_saldo_conta_pagar ( nr_seq_conta_p bigint, vl_pago_p bigint, ds_erro_p INOUT text, cd_estabelecimento_p bigint) AS $body$
DECLARE

ie_saldo_baixa_w		varchar(3);
ds_erro_w		varchar(255);
vl_saldo_w		double precision;

BEGIN
if (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') then
	begin
	select 	coalesce(ie_saldo_baixa_bco,'N')
	into STRICT 	ie_saldo_baixa_w
	from	parametros_contas_pagar
	where 	cd_estabelecimento = cd_estabelecimento_p;
	end;
	if (ie_saldo_baixa_w = 'S') then
		begin
		select 	obter_saldo_conta_concil(nr_seq_conta_p, clock_timestamp(), 'T')
		into STRICT	vl_saldo_w
		;
		end;
	elsif (ie_saldo_baixa_w = 'SC') then
		begin
		select 	obter_saldo_conta_concil(nr_seq_conta_p, clock_timestamp(), 'S')
		into STRICT	vl_saldo_w
		;
		end;
	end if;
	if (ie_saldo_baixa_w <> 'N') and ((vl_pago_p IS NOT NULL AND vl_pago_p::text <> '') and
		vl_pago_p > vl_saldo_w)then
		begin
		ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(282232,'VL_SALDO='||campo_mascara_virgula(vl_saldo_w) );
		end;
	end if;
ds_erro_p := ds_erro_w;
end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
