SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE verifica_senha_inativa (nm_usuario_p text) AS $body$
DECLARE


    nr_seq_fila_w          bigint;
    qt_tempo_finalizacao_w bigint;

    c01 CURSOR FOR
        SELECT nr_sequencia,
               qt_tempo_finalizacao
          FROM fila_espera_senha
         WHERE coalesce(qt_tempo_finalizacao, 0) > 0;


BEGIN

    OPEN c01;
    LOOP
        FETCH c01
            INTO nr_seq_fila_w,
                 qt_tempo_finalizacao_w;
        EXIT WHEN NOT FOUND; /* apply on c01 */

        UPDATE paciente_senha_fila
           SET dt_fim_atendimento = clock_timestamp(),
               nm_usuario         = nm_usuario_p
         WHERE nr_seq_fila_senha = nr_seq_fila_w
           AND ((clock_timestamp() - dt_inicio_atendimento) * 1440) > qt_tempo_finalizacao_w
		   AND coalesce(dt_fim_atendimento::text, '') = '';

    END LOOP;
    CLOSE c01;

    COMMIT;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
