SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE verifica_se_idiomas_validados ( nr_sequencia_p bigint) AS $body$
DECLARE


qt_pend_validacao_w	bigint;


BEGIN

	select	count(nr_sequencia)
	into STRICT	qt_pend_validacao_w
	from	qua_documento
	where	nr_seq_superior = nr_sequencia_p
	and	coalesce(dt_validacao::text, '') = ''
	and	coalesce(ie_situacao, 'A') = 'A';

	if (qt_pend_validacao_w > 0) then
		CALL Wheb_mensagem_pck.exibir_mensagem_abort(306880);
	end if;
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
