SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE verifica_se_prescr_atend (nr_prescricao_p bigint) AS $body$
DECLARE


ie_prescr_atend_w varchar(1);


BEGIN

select	coalesce(max('S'),'N')
into STRICT	ie_prescr_atend_w
from	prescr_material
where	nr_prescricao = nr_prescricao_p
and		cd_motivo_baixa > 0;

if (ie_prescr_atend_w = 'S') then
	-- Esta prescrição já foi atendida e não será possível realizar esta ação!
	CALL Wheb_mensagem_pck.exibir_mensagem_abort( 261352 );
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
