CREATE OR REPLACE PROCEDURE verifica_solicitacao_analista ( nm_usuario_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


qt_reg_w		integer;
ds_comando_w	varchar(2000);
ds_parametros_w	varchar(2000);

BEGIN

ds_comando_w := ''||
	'select	count(*) ' ||
	'from	man_solicitacao_auxilio ' ||
	'where	UPPER(nm_usuario_analista) = UPPER(:nm_usuario) '||
	'and	ie_status = ''P''';

ds_parametros_w := 'nm_usuario='||nm_usuario_p;

qt_reg_w := obter_valor_dinamico_bv(ds_comando_w, ds_parametros_w, qt_reg_w);

ds_retorno_p	:= qt_reg_w;

END	;
