SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE verifica_tipo_barras ( cd_barras_p text, ie_forcar_verificacao_p text default 'N', ie_tipo_p INOUT text DEFAULT NULL, -- É o id do código de barras. Ex: "P00", "P0B".
 ds_tipo_p INOUT text) as  -- É a descrição do tipo de id  do barras. Ex: "DataMatrix", "UPU FICS Postal".
/*
ie_forcar_verificacao_p
	- Adicionado esta opção para a identificação dos códigos DataMatrix na função Administração de Estoques pasta Lote Fornecedor;
	- Desta forma, será utilizada esta opção para forçar a verificação do tipo do barras;
*/
 ds_id_w varchar(20 DEFAULT NULL) AS $body$
DECLARE

PERFORM	ds_id,
	ds_tipo
from	id_codigo_barra;


BEGIN
/*Colocado fixo por Fabio e Sidnei porque estamos mudando a forma de implementação do DataMatrix*/

ie_tipo_w	:= 'N';

if (obter_funcao_ativa in (40,270)) or (coalesce(ie_forcar_verificacao_p,'N') = 'S') then
	begin
	ie_encontrou_w	:= 'N'; --Quer dizer que não encontrou nada no cadastro dos tipos
	ie_tipo_w	:= 'N'; --Quer dizer que não é tipo normal, ou seja: não encontrou nenhum tipo específico para tratamento
	open c01;
	loop fetch c01
	into	ds_id_w,
		ds_tipo_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		if (ie_encontrou_w	= 'N') then
			begin
			qt_tamanho_w	:= length(ds_id_w);

			if (substr(cd_barras_p, 1, qt_tamanho_w) = ds_id_w) then
				begin
				ie_encontrou_w		:= 'S';
				ie_tipo_w		:= ds_id_w;
				ds_tipo_id_w		:= ds_tipo_w;

				/*if	(ds_tipo_w = 'DataMatrix') then
					ie_tipo_w		:= 'P00';
				end if;*/
				end;
			end if;
			end;
		end if;
		end;
	end loop;
	close c01;
	end;
end if;

ie_tipo_p	:= substr(ie_tipo_w,1,20);
ds_tipo_p	:= substr(ds_tipo_id_w,1,255);
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE verifica_tipo_barras ( cd_barras_p text, ie_forcar_verificacao_p text default 'N', ie_tipo_p INOUT text DEFAULT NULL, ds_tipo_p INOUT text) as  ds_id_w varchar(20 DEFAULT NULL) FROM PUBLIC;

