CREATE OR REPLACE PROCEDURE verify_current_pat_category ( nr_atendimento_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, nm_usuario_p text, dt_procedimento_p timestamp default clock_timestamp()) AS $body$
DECLARE



ie_patient_class_w				procedimento.ie_patient_class%type;
nr_seq_patient_category_w		patient_class_category.nr_seq_patient_category%type;
nr_seq_interno_w				atend_categoria_convenio.nr_seq_interno%type := null;
nr_seq_patient_category_conv_w	atend_categoria_convenio.nr_seq_patient_category%type;


BEGIN

select	max(ie_patient_class)
into STRICT	ie_patient_class_w
from	procedimento
where	cd_procedimento = cd_procedimento_p
and		ie_origem_proced = ie_origem_proced_p;

if (ie_patient_class_w IS NOT NULL AND ie_patient_class_w::text <> '') then
	begin

	select	max(nr_seq_patient_category)
	into STRICT	nr_seq_patient_category_w
	from	patient_class_category
	where	coalesce(ie_situacao,'A') = 'A'
	and		ie_patient_class = ie_patient_class_w;


	if (nr_seq_patient_category_w IS NOT NULL AND nr_seq_patient_category_w::text <> '') then
		begin

		begin
		select	nr_seq_interno,
				nr_seq_patient_category
		into STRICT	nr_seq_interno_w,
				nr_seq_patient_category_conv_w
		from 	atend_categoria_convenio
		where 	nr_atendimento	= nr_atendimento_p
		and		dt_inicio_vigencia = (SELECT	max(dt_inicio_vigencia)
									  from 		atend_categoria_convenio x
									  where		x.nr_atendimento = nr_atendimento_p);
		exception
		when others then
			nr_seq_interno_w := null;
		end;

		if (nr_seq_interno_w IS NOT NULL AND nr_seq_interno_w::text <> '') and (nr_seq_patient_category_conv_w <> nr_seq_patient_category_w) then
			begin

			update	atend_categoria_convenio
			set		nr_seq_patient_category = nr_seq_patient_category_w,
					nm_usuario 				= nm_usuario_p,
					dt_atualizacao 			= clock_timestamp(),
					EKVK_DT_INICIO   =      dt_procedimento_p
			where	nr_seq_interno = nr_seq_interno_w
			and		nr_atendimento = nr_atendimento_p;
			
			end;
		end if;


		end;
	end if;
	
	
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
