CREATE OR REPLACE PROCEDURE verif_considerar_regras_agecir ( nr_seq_agenda_p bigint, ds_justif_consid_regra_p text, nm_usuario_p text, ie_desconsiderar_regras_p text) AS $body$
DECLARE


/*
IE_DESCONSIDERAR_REGRAS
N - Nao Considera as regras para o horario especifico (usado a opcao de mouse "Desconsiderar Regra")
S - Considera as regras para o horario especifico (usado a opcao de mouse "Considerar Regra")
*/
nr_sequencia_w			bigint;


BEGIN

if (coalesce(nr_seq_agenda_p,0) > 0) then
	SELECT	coalesce(max(nr_sequencia),0)
	INTO STRICT	nr_sequencia_w
	FROM	considerar_regras_agecir
	WHERE	nr_seq_agenda	=	nr_seq_agenda_p;


	IF (nr_sequencia_w > 0) THEN
		UPDATE	considerar_regras_agecir
        SET 	ie_desconsiderar_regras = ie_desconsiderar_regras_p
        WHERE 	nr_sequencia = nr_sequencia_w;

	ELSE
		begin
			select	nextval('considerar_regras_agecir_seq')
			into STRICT	nr_sequencia_w
			;
			
			insert into considerar_regras_agecir(
				nr_sequencia,
				NR_SEQ_AGENDA,
				DT_ATUALIZACAO,
				NM_USUARIO,
				DS_JUSTIF_CONSID_REGRA,
				DT_ATUALIZACAO_NREC,
				NM_USUARIO_NREC,
				IE_DESCONSIDERAR_REGRAS )
			values (
				nr_sequencia_w,
				nr_seq_agenda_p,
				clock_timestamp(),
				nm_usuario_p,
				ds_justif_consid_regra_p,
				clock_timestamp(),
				nm_usuario_p,
				ie_desconsiderar_regras_p);
		end;
    END IF;

	COMMIT;
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
