SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE verif_se_atend_prescr_bloqueio ( nr_prescricao_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint, ds_erro_p INOUT text) AS $body$
DECLARE


/*
Esta procedure retorna se um lote está bloqueado para determinado usuário.
O bloqueio acontece sempre após a localizacão do lote pelo usuário, se o usuário localizar outro lote o atual é desbloqueado.
Também é tratado um determinado tempo para o desbloqueio automático, pois um usuário pode deixar a tela aberta com um lote bloqueado para si.
*/
nr_sequencia_w		bigint;
nm_usuario_w		varchar(15);
dt_atualizacao_w		timestamp;
dt_tentativa_w		timestamp;
ie_bloqueado_w		varchar(1);
qt_min_controle_prescr_w	bigint;
qt_min_diferenca_w		double precision;
ds_retorno_w		varchar(255);


BEGIN
ie_bloqueado_w	:= 'N';

select	coalesce(max(qt_min_controle_prescr), 0)
into STRICT	qt_min_controle_prescr_w
from	parametros_farmacia
where	cd_estabelecimento = cd_estabelecimento_p;

if (qt_min_controle_prescr_w > 0) then
	begin
	delete
	from	at_prescr_bloqueio
	where	nm_usuario	= nm_usuario_p
	and	nr_prescricao	<> nr_prescricao_p;

	select	coalesce(max(nr_sequencia), 0)
	into STRICT	nr_sequencia_w
	from	at_prescr_bloqueio
	where	nr_prescricao	= nr_prescricao_p;

	if (nr_sequencia_w = 0) then

		insert into at_prescr_bloqueio(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			nr_prescricao)
		values (	nextval('at_prescr_bloqueio_seq'),
			clock_timestamp(),
			nm_usuario_p,
			nr_prescricao_p);

		ie_bloqueado_w	:= 'N';
	else
		begin
		select	nm_usuario,
			dt_atualizacao
		into STRICT	nm_usuario_w,
			dt_atualizacao_w
		from	at_prescr_bloqueio
		where	nr_sequencia	= nr_sequencia_w;

		if (nm_usuario_w = nm_usuario_p) then

			update	at_prescr_bloqueio
			set	dt_atualizacao	= clock_timestamp()
			where	nr_sequencia	= nr_sequencia_w;

			ie_bloqueado_w	:= 'N';

		elsif (nm_usuario_w <> nm_usuario_p) then
			begin
			dt_tentativa_w		:= clock_timestamp();
			qt_min_diferenca_w	:= ((dt_tentativa_w - dt_atualizacao_w) * 1440);

			if (qt_min_diferenca_w < qt_min_controle_prescr_w) then
				ie_bloqueado_w	:= 'S';
			else
				update	at_prescr_bloqueio
				set	nm_usuario	= nm_usuario_p,
					dt_atualizacao	= dt_tentativa_w
				where	nr_sequencia	= nr_sequencia_w;

				ie_bloqueado_w	:= 'N';
			end if;
			end;
		end if;
		end;
	end if;
	end;
end if;

if (ie_bloqueado_w = 'S') then
	ds_erro_p		:= WHEB_MENSAGEM_PCK.get_texto(310805) || nm_usuario_w || ' !' ||  chr(13) || chr(13) ||
				WHEB_MENSAGEM_PCK.get_texto(281143) || to_char(dt_atualizacao_w,'dd/mm/yyyy hh24:mi:ss') || chr(13) || chr(10) ||
				WHEB_MENSAGEM_PCK.get_texto(281144) || to_char(dt_tentativa_w, 'dd/mm/yyyy hh24:mi:ss') || chr(13) || chr(10) ||
				WHEB_MENSAGEM_PCK.get_texto(281145) || qt_min_controle_prescr_w || WHEB_MENSAGEM_PCK.get_texto(281146);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
