CREATE OR REPLACE PROCEDURE via_cancelar_reserva (nr_seq_reserva_p text, ds_motivo_cancel_p text, ie_respo_custo_p text, nr_seq_tipo_motivo_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_reserva_trans_w	bigint;
c01 CURSOR FOR
SELECT   b.nr_sequencia
from     via_transporte a,
         via_reserva_transporte b
where  a.nr_sequencia = b.nr_seq_transporte
and    b.nr_seq_reserva = nr_seq_reserva_p;




BEGIN

UPDATE 	via_reserva
SET	ie_cancelamento = 'C',
	nm_usuario = nm_usuario_p,
	dt_atualizacao = clock_timestamp()
