					   nm_usuario,
					   dt_atualizacao_nrec,
					   nm_usuario_nrec,
					   nr_seq_reserva,
					   ds_motivo_cancel,
					   nm_usuario_cancel,
					   ie_responsavel_custo,
					   nr_seq_tipo_motivo,
					   nr_seq_reserva_trans)
	VALUES (nextval('via_reserva_cancelada_seq'),
					   clock_timestamp(),
					   nm_usuario_p,
					   clock_timestamp(),
					   nm_usuario_p,
					   nr_seq_reserva_p,
					   ds_motivo_cancel_p,
					   nm_usuario_p,
