CREATE OR REPLACE PROCEDURE via_gerar_reserva_fatura ( nr_seq_fatura_p bigint, nr_seq_reserva_p bigint, nm_usuario_p text) AS $body$
DECLARE

vl_reserva_w	double precision:= 0;


BEGIN

select 	coalesce(obter_valor_total_hotel(nr_sequencia),0)
into STRICT	vl_reserva_w
from 	via_reserva
where	nr_sequencia = nr_seq_reserva_p;

insert into via_fat_reserva(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_fatura,
			nr_seq_reserva,
			vl_reserva)
		values (	nextval('via_fat_reserva_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_fatura_p,
			nr_seq_reserva_p,
			vl_reserva_w);

commit;

end;
