SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE via_vincular_rat ( nr_seq_rat_p bigint, nr_seq_viagem_p bigint, nm_usuario_p text, ie_opcao_p text) AS $body$
DECLARE


ie_existe_rat_w	via_viagem.ie_etapa_viagem%type := 'N';

/*
IE_OPCAO_P
V - Vincular
D - Desvincular
*/
BEGIN

if ((nr_seq_rat_p IS NOT NULL AND nr_seq_rat_p::text <> '')
		and (nr_seq_viagem_p IS NOT NULL AND nr_seq_viagem_p::text <> '')
		and (ie_opcao_p IS NOT NULL AND ie_opcao_p::text <> '')) then
	        
	select	CASE WHEN count(1)=1 THEN  'S'  ELSE 'N' END 
	into STRICT	ie_existe_rat_w
	
	where	exists (
		SELECT	1
		from	via_rat
		where	nr_seq_rat = nr_seq_rat_p
		and	nr_seq_viagem = nr_seq_viagem_p);

	if (ie_opcao_p = 'V'
			and ie_existe_rat_w = 'N') then
		insert into via_rat(
			nr_sequencia,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			nr_seq_viagem,
			nr_seq_rat)
		values (
			nextval('via_rat_seq'),
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_viagem_p,
			nr_seq_rat_p);

	elsif (ie_opcao_p = 'D'
			and ie_existe_rat_w = 'S') then
		delete	FROM via_rat
		where	nr_seq_rat = nr_seq_rat_p
		and	nr_seq_viagem = nr_seq_viagem_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
