CREATE OR REPLACE PROCEDURE vincular_adiantamento_cheque ( nr_adiantamento_p bigint, nr_seq_cheque_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_registro_w	bigint := 0;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from (
	SELECT	1
	from	cheque_cr a
	where	a.nr_seq_cheque	= nr_seq_cheque_p
	and	a.nr_adiantamento	= nr_adiantamento_p
	
union

	SELECT	1
	from	adiantamento_cheque_cr a
	where	a.nr_seq_cheque	= nr_seq_cheque_p
	and	a.nr_adiantamento	= nr_adiantamento_p
	) alias1;

if (qt_registro_w = 0) then
	insert	into
		adiantamento_cheque_cr(nr_sequencia,
	dt_atualizacao,
	nm_usuario,
	nr_seq_cheque,
	nr_adiantamento
	)
	values (nextval('adiantamento_cheque_cr_seq'),
	clock_timestamp(),
	nm_usuario_p,
	nr_seq_cheque_p,
	nr_adiantamento_p);

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
