CREATE OR REPLACE PROCEDURE vincular_atendimento_agenda (nr_atendimento_p bigint, cd_convenio_p INOUT bigint, cd_pessoa_fisica_p INOUT text, cd_plano_p INOUT text, cd_usuario_convenio_p INOUT text, ds_erro_p INOUT text) AS $body$
DECLARE

 
cd_convenio_w		bigint;
cd_convenio_param_w	bigint;
cd_pessoa_fisica_w	varchar(10);
cd_plano_w		varchar(20);
cd_usuario_convenio_w	varchar(30);
dt_fim_conta_w		timestamp;
qt_reg_w		bigint;
ds_erro_w		varchar(255)	:= null;
ie_perm_conta_def_w	varchar(1);
cd_funcao_ativa_w	bigint := obter_funcao_ativa;

c01 CURSOR FOR 
	SELECT	a.cd_pessoa_fisica, 
			a.dt_fim_conta, 
			b.cd_convenio, 
			b.cd_usuario_convenio, 
			b.cd_plano_convenio 
	from	atend_categoria_convenio b, 
			atendimento_paciente a 
	where	a.nr_atendimento	= b.nr_atendimento 
	and		a.nr_atendimento	= nr_atendimento_p 
	and		b.cd_convenio		= coalesce(cd_convenio_param_w, b.cd_convenio);


BEGIN 
 
ie_perm_conta_def_w := Obter_param_Usuario(866, 173, obter_perfil_ativo, wheb_usuario_pck.get_nm_usuario, wheb_usuario_pck.get_cd_estabelecimento, ie_perm_conta_def_w);
 
cd_convenio_param_w	:= null;
 
select	count(*) 
into STRICT	qt_reg_w 
from	atend_categoria_convenio 
where	nr_atendimento	= nr_atendimento_p 
and		cd_convenio	= cd_convenio_p;
 
if (qt_reg_w	> 0) then 
	cd_convenio_param_w	:= cd_convenio_p;
end if;
 
open	c01;
loop 
fetch	c01 into 
		cd_pessoa_fisica_w, 
		dt_fim_conta_w, 
		cd_convenio_w, 
		cd_usuario_convenio_w, 
		cd_plano_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
 
	if (dt_fim_conta_w IS NOT NULL AND dt_fim_conta_w::text <> '') then 
		if	((cd_funcao_ativa_w <> 866 and cd_funcao_ativa_w <> 281) or (ie_perm_conta_def_w = 'N'))	then 
			ds_erro_w	:= WHEB_MENSAGEM_PCK.get_texto(279682,null);
		end if;
	end if;
 
	end;
end loop;
close c01;
 
cd_pessoa_fisica_p		:= cd_pessoa_fisica_w;
cd_convenio_p			:= cd_convenio_w;
cd_usuario_convenio_p	:= cd_usuario_convenio_w;
cd_plano_p				:= cd_plano_w;
ds_erro_p				:= ds_erro_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
